// Enhanced Input validation with new fields
const requiredFields = ['platform', 'purpose', 'target_audience', 'script_length', 'key_message', 'cta', 'language'];
const optionalFields = ['tone', 'brand_context', 'content_type', 'industry', 'campaign_type', 'location'];

// Get input data from webhook
const webhookData = $input.first().json;
const input = webhookData.body || webhookData;

// Check if input exists
if (!input) {
  return [{
    json: {
      error: true,
      message: 'No input data received',
      status: 'validation_failed'
    }
  }];
}

// Validate required fields
for (const field of requiredFields) {
  if (!input[field] || input[field].toString().trim() === '') {
    return [{
      json: {
        error: true,
        message: `Missing required field: ${field}`,
        status: 'validation_failed'
      }
    }];
  }
}

// Set defaults for optional fields
const processedInput = {
  ...input,
  tone: input.tone || 'engaging',
  content_type: input.content_type || 'video script',
  industry: input.industry || 'general',
  campaign_type: input.campaign_type || 'brand awareness',
  location: input.location || 'global',
  brand_context: input.brand_context || ''
};

// Language flexibility - handle mixed languages
if (processedInput.language) {
  processedInput.language = processedInput.language.toLowerCase().trim();
  // Accept various language formats
  const languageMap = {
    'hinglish': 'Hindi-English mix (Hinglish)',
    'spanglish': 'Spanish-English mix (Spanglish)',
    'english': 'English',
    'hindi': 'Hindi',
    'spanish': 'Spanish',
    'french': 'French',
    'german': 'German',
    'italian': 'Italian',
    'portuguese': 'Portuguese',
    'japanese': 'Japanese',
    'korean': 'Korean',
    'chinese': 'Chinese',
    'arabic': 'Arabic'
  };

  processedInput.language = languageMap[processedInput.language] || processedInput.language;
}

return [{
  json: {
    input: processedInput,
    validation_passed: true,
    timestamp: new Date().toISOString()
  }
}];

// Platform-specific optimization and constraints
