// Parse and validate webhook input
const webhookData = $input.all()[0].json;

// Extract competitor domains (can be single domain or array)
let domains = [];
if (typeof webhookData.competitorDomain === 'string') {
  domains = [webhookData.competitorDomain];
} else if (Array.isArray(webhookData.competitorDomain)) {
  domains = webhookData.competitorDomain;
}

const industry = webhookData.industry || webhookData.niche || 'General';

// Create individual items for each domain
const outputItems = domains.map(domain => ({
  json: {
    domain: domain.replace(/^https?:\/\//, '').replace(/\/.*$/, ''), // Clean domain
    industry: industry,
    originalInput: webhookData
  }
}));

return outputItems;

// Process and normalize all API responses
const items = $input.all();
const processedData = [];

for (const item of items) {
  const data = item.json;

  // Extract domain info from input processor
  const domain = data.domain || data.url || 'unknown';
  const industry = data.industry || 'General';

  // Initialize result object
  let result = {
    domain: domain,
    industry: industry,
    timestamp: new Date().toISOString(),
    technologies: [],
    cms: null,
    analytics: [],
    adNetworks: [],
    ecommerce: [],
    seoMetrics: {},
    domainInfo: {},
    socialMetadata: {},
    performanceMetrics: {}
  };

  // Process Wappalyzer data
  if (data.technologies || (data.body && data.body.includes && data.body.includes('technologies'))) {
    let wappalyzerData = [];

    if (data.technologies) {
      wappalyzerData = data.technologies;
    } else if (data.body) {
      try {
        const parsed = JSON.parse(data.body);
        wappalyzerData = parsed.technologies || [];
      } catch (e) {
        wappalyzerData = [];
      }
    }

    for (const tech of wappalyzerData) {
      const techInfo = {
        name: tech.name,
        category: tech.categories && tech.categories[0] ? tech.categories[0].name : 'Other',
        version: tech.version || null,
        confidence: tech.confidence || 100,
        source: 'wappalyzer'
      };

      result.technologies.push(techInfo);

      // Categorize technologies
      const category = techInfo.category.toLowerCase();

      if (category.includes('cms') || category.includes('content management')) {
        result.cms = techInfo;
      } else if (category.includes('analytics')) {
        result.analytics.push(techInfo);
      } else if (category.includes('advertising') || category.includes('ad network')) {
        result.adNetworks.push(techInfo);
      } else if (category.includes('ecommerce')) {
        result.ecommerce.push(techInfo);
      }
    }
  }

  // Process DataForSEO data
  if (data.tasks && data.tasks[0] && data.tasks[0].result) {
    const seoData = data.tasks[0].result[0];

    if (seoData && seoData.technologies) {
      for (const tech of seoData.technologies) {
        result.technologies.push({
          name: tech.technology,
          category: tech.category || 'Other',
          source: 'dataforseo'
        });
      }
    }

    if (seoData) {
      result.seoMetrics = {
        organicKeywords: seoData.organic_keywords || 0,
        paidKeywords: seoData.paid_keywords || 0,
        organicTraffic: seoData.organic_traffic || 0,
        paidTraffic: seoData.paid_traffic || 0
      };
    }
  }

  // Process WhoisXML data
  if (data.WhoisRecord) {
    const whoisData = data.WhoisRecord;
    result.domainInfo = {
      registrar: whoisData.registrarName,
      createdDate: whoisData.createdDate,
      updatedDate: whoisData.updatedDate,
      expiresDate: whoisData.expiresDate,
      nameServers: whoisData.nameServers && whoisData.nameServers.hostNames ? whoisData.nameServers.hostNames : []
    };
  }

  // Process OpenGraph data (from HTML scraping)
  if (data.body && typeof data.body === 'string') {
    const html = data.body;

    // Extract OpenGraph meta tags
    const ogTitleMatch = html.match(/<meta property="og:title" content="([^"]+)"/);
    const ogDescMatch = html.match(/<meta property="og:description" content="([^"]+)"/);
    const ogImageMatch = html.match(/<meta property="og:image" content="([^"]+)"/);
    const ogTypeMatch = html.match(/<meta property="og:type" content="([^"]+)"/);

    result.socialMetadata = {
      title: ogTitleMatch ? ogTitleMatch[1] : null,
      description: ogDescMatch ? ogDescMatch[1] : null,
      image: ogImageMatch ? ogImageMatch[1] : null,
      type: ogTypeMatch ? ogTypeMatch[1] : null
    };

    // Check for common technologies in HTML
    const htmlTechs = [];
    if (html.includes('google-analytics.com') || html.includes('gtag') || html.includes('ga(')) {
      htmlTechs.push({ name: 'Google Analytics', category: 'Analytics', source: 'html_analysis' });
    }
    if (html.includes('facebook.net') || html.includes('fbq')) {
      htmlTechs.push({ name: 'Facebook Pixel', category: 'Analytics', source: 'html_analysis' });
    }
    if (html.includes('jquery')) {
      htmlTechs.push({ name: 'jQuery', category: 'JavaScript Library', source: 'html_analysis' });
    }

    result.technologies = result.technologies.concat(htmlTechs);
  }

  // Process PageSpeed data
  if (data.lighthouseResult) {
    const lighthouse = data.lighthouseResult;

    result.performanceMetrics = {
      performanceScore: lighthouse.categories && lighthouse.categories.performance ? lighthouse.categories.performance.score * 100 : 0,
      seoScore: lighthouse.categories && lighthouse.categories.seo ? lighthouse.categories.seo.score * 100 : 0,
      firstContentfulPaint: lighthouse.audits && lighthouse.audits['first-contentful-paint'] ? lighthouse.audits['first-contentful-paint'].numericValue : 0,
      largestContentfulPaint: lighthouse.audits && lighthouse.audits['largest-contentful-paint'] ? lighthouse.audits['largest-contentful-paint'].numericValue : 0,
      cumulativeLayoutShift: lighthouse.audits && lighthouse.audits['cumulative-layout-shift'] ? lighthouse.audits['cumulative-layout-shift'].numericValue : 0,
      speedIndex: lighthouse.audits && lighthouse.audits['speed-index'] ? lighthouse.audits['speed-index'].numericValue : 0
    };
  }

  // Deduplicate technologies
  const uniqueTechs = [];
  const seenTechs = new Set();

  for (const tech of result.technologies) {
    const key = tech.name.toLowerCase() + '-' + tech.category.toLowerCase();
    if (!seenTechs.has(key)) {
      seenTechs.add(key);
      uniqueTechs.push(tech);
    }
  }

  result.technologies = uniqueTechs;

  processedData.push({ json: result });
}

return processedData;

// Aggregate data for dashboard visualization
const allData = $input.all().map(item => item.json);

// Initialize dashboard data structure
const dashboardData = {
  summary: {
    totalDomains: allData.length,
    industry: allData[0] ? allData[0].industry : 'General',
    analysisTimestamp: new Date().toISOString(),
    totalTechnologies: 0,
    uniqueTechnologies: 0
  },

  // CMS Distribution Data
  cmsDistribution: {},

  // Analytics Tools Distribution
  analyticsDistribution: {},

  // Ad Networks Distribution
  adNetworksDistribution: {},

  // Technology Categories Distribution
  categoryDistribution: {},

  // Performance Metrics Averages
  performanceAverages: {
    performanceScore: 0,
    seoScore: 0,
    firstContentfulPaint: 0,
    largestContentfulPaint: 0
  },

  // Individual domain details
  domainDetails: [],

  // Technology popularity ranking
  technologyRanking: {},

  // SEO metrics summary
  seoSummary: {
    avgOrganicKeywords: 0,
    avgPaidKeywords: 0,
    avgOrganicTraffic: 0,
    avgPaidTraffic: 0
  }
};

const uniqueTechSet = new Set();

// Process each domain's data
for (const domainData of allData) {
  dashboardData.domainDetails.push(domainData);

  // Count CMS usage
  if (domainData.cms) {
    const cmsName = domainData.cms.name;
    dashboardData.cmsDistribution[cmsName] = (dashboardData.cmsDistribution[cmsName] || 0) + 1;
  } else {
    dashboardData.cmsDistribution['Unknown/Custom'] = (dashboardData.cmsDistribution['Unknown/Custom'] || 0) + 1;
  }

  // Count analytics tools
  for (const analytics of domainData.analytics) {
    dashboardData.analyticsDistribution[analytics.name] = (dashboardData.analyticsDistribution[analytics.name] || 0) + 1;
  }

  // Count ad networks
  for (const adNetwork of domainData.adNetworks) {
    dashboardData.adNetworksDistribution[adNetwork.name] = (dashboardData.adNetworksDistribution[adNetwork.name] || 0) + 1;
  }

  // Count technology categories and individual technologies
  for (const tech of domainData.technologies) {
    uniqueTechSet.add(tech.name);
    dashboardData.categoryDistribution[tech.category] = (dashboardData.categoryDistribution[tech.category] || 0) + 1;
    dashboardData.technologyRanking[tech.name] = (dashboardData.technologyRanking[tech.name] || 0) + 1;
  }

  dashboardData.summary.totalTechnologies += domainData.technologies.length;

  // Aggregate performance metrics
  if (domainData.performanceMetrics) {
    dashboardData.performanceAverages.performanceScore += domainData.performanceMetrics.performanceScore || 0;
    dashboardData.performanceAverages.seoScore += domainData.performanceMetrics.seoScore || 0;
    dashboardData.performanceAverages.firstContentfulPaint += domainData.performanceMetrics.firstContentfulPaint || 0;
    dashboardData.performanceAverages.largestContentfulPaint += domainData.performanceMetrics.largestContentfulPaint || 0;
  }

  // Aggregate SEO metrics
  if (domainData.seoMetrics) {
    dashboardData.seoSummary.avgOrganicKeywords += domainData.seoMetrics.organicKeywords || 0;
    dashboardData.seoSummary.avgPaidKeywords += domainData.seoMetrics.paidKeywords || 0;
    dashboardData.seoSummary.avgOrganicTraffic += domainData.seoMetrics.organicTraffic || 0;
    dashboardData.seoSummary.avgPaidTraffic += domainData.seoMetrics.paidTraffic || 0;
  }
}

// Calculate averages
const domainCount = allData.length;
if (domainCount > 0) {
  dashboardData.performanceAverages.performanceScore = dashboardData.performanceAverages.performanceScore / domainCount;
  dashboardData.performanceAverages.seoScore = dashboardData.performanceAverages.seoScore / domainCount;
  dashboardData.performanceAverages.firstContentfulPaint = dashboardData.performanceAverages.firstContentfulPaint / domainCount;
  dashboardData.performanceAverages.largestContentfulPaint = dashboardData.performanceAverages.largestContentfulPaint / domainCount;

  dashboardData.seoSummary.avgOrganicKeywords = dashboardData.seoSummary.avgOrganicKeywords / domainCount;
  dashboardData.seoSummary.avgPaidKeywords = dashboardData.seoSummary.avgPaidKeywords / domainCount;
  dashboardData.seoSummary.avgOrganicTraffic = dashboardData.seoSummary.avgOrganicTraffic / domainCount;
  dashboardData.seoSummary.avgPaidTraffic = dashboardData.seoSummary.avgPaidTraffic / domainCount;
}

// Set unique technologies count
dashboardData.summary.uniqueTechnologies = uniqueTechSet.size;

// Sort technology ranking by usage
const sortedTechRanking = {};
Object.keys(dashboardData.technologyRanking)
  .sort((a, b) => dashboardData.technologyRanking[b] - dashboardData.technologyRanking[a])
  .forEach(key => {
    sortedTechRanking[key] = dashboardData.technologyRanking[key];
  });
dashboardData.technologyRanking = sortedTechRanking;

// Create visualization-ready data structures
dashboardData.visualizations = {
  // Stacked bar chart data for CMS distribution
  cmsChart: {
    type: 'stackedBar',
    data: Object.keys(dashboardData.cmsDistribution).map(cms => {
      const count = dashboardData.cmsDistribution[cms];
      return {
        cms: cms,
        count: count,
        percentage: Number(((count / domainCount) * 100).toFixed(1))
      };
    })
  },

  // Pie chart data for analytics tools
  analyticsChart: {
    type: 'pie',
    data: Object.keys(dashboardData.analyticsDistribution).map(tool => {
      const count = dashboardData.analyticsDistribution[tool];
      return {
        tool: tool,
        count: count,
        percentage: Number(((count / domainCount) * 100).toFixed(1))
      };
    })
  },

  // Technology category distribution
  categoryChart: {
    type: 'horizontalBar',
    data: Object.keys(dashboardData.categoryDistribution)
      .sort((a, b) => dashboardData.categoryDistribution[b] - dashboardData.categoryDistribution[a])
      .slice(0, 15)
      .map(category => {
        return {
          category: category,
          count: dashboardData.categoryDistribution[category]
        };
      })
  },

  // Performance comparison chart
  performanceChart: {
    type: 'multiBar',
    data: allData.map(domain => {
      return {
        domain: domain.domain,
        performanceScore: domain.performanceMetrics ? (domain.performanceMetrics.performanceScore || 0) : 0,
        seoScore: domain.performanceMetrics ? (domain.performanceMetrics.seoScore || 0) : 0
      };
    })
  },

  // Technology adoption heatmap
  technologyHeatmap: {
    type: 'heatmap',
    data: Object.keys(dashboardData.technologyRanking)
      .slice(0, 20)
      .map(tech => {
        const count = dashboardData.technologyRanking[tech];
        return {
          technology: tech,
          adoptionRate: Number(((count / domainCount) * 100).toFixed(1)),
          count: count
        };
      })
  }
};

return [{ json: dashboardData }];
