// Extended Validate & Parse Input node with sector/theme mapping
const inputData = $input.first().json;

// Extract parameters
let market = inputData.market?.toLowerCase();
let location = inputData.location?.toUpperCase();
const timeRange = inputData.time_range || inputData.timeRange;

// Validation for base fields
if (!market || !timeRange) {
  return [{
    json: {
      error: true,
      message: "Missing required fields: market, time_range",
      requiredFormat: {
        market: "stock/crypto/forex/commodity/sector/index/fund/etc",
        location: "Optional for sectors/indices/funds",
        time_range: "1W/1M/3M/6M/1Y/2Y"
      }
    }
  }];
}

// Country name to code mapping
const countryMapping = {
  'UNITED STATES': 'US', 'USA': 'US', 'AMERICA': 'US', 'CANADA': 'CA',
  'UNITED KINGDOM': 'UK', 'UK': 'UK', 'ENGLAND': 'UK',
  'GERMANY': 'DE', 'FRANCE': 'FR', 'ITALY': 'IT', 'SPAIN': 'ES',
  'INDIA': 'IN', 'CHINA': 'CN', 'JAPAN': 'JP', 'SOUTH KOREA': 'KR'
};
location = countryMapping[location] || location;

// Valid time ranges
const validTimeRanges = ['1W', '1M', '3M', '6M', '1Y', '2Y'];
if (!validTimeRanges.includes(timeRange)) {
  return [{
    json: {
      error: true,
      message: `Invalid time range. Valid options: ${validTimeRanges.join(', ')}`
    }
  }];
}

// Market type mapping
const marketTypes = {
  'stock': 'stock', 'stocks': 'stock', 'equity': 'stock',
  'crypto': 'crypto', 'cryptocurrency': 'crypto',
  'forex': 'forex', 'currency': 'forex',
  'commodity': 'commodity', 'commodities': 'commodity',
  // new types
  'sector': 'sector', 'index': 'index', 'fund': 'fund'
};

// Theme mapping to symbols
const themeSymbols = {
  // Sectors
  'energy': 'XLE', 'technology': 'XLK', 'financials': 'XLF',
  'healthcare': 'XLV', 'utilities': 'XLU', 'consumer goods': 'XLP',
  'consumer services': 'XLY', 'industrials': 'XLI', 'materials': 'XLB',
  'real estate': 'XLRE', 'communications': 'XLC',

  // Commodities
  'gold': 'GC=F', 'silver': 'SI=F', 'oil': 'CL=F',
  'natural gas': 'NG=F', 'copper': 'HG=F',

  // Indices
  'sp500': '^GSPC', 'nasdaq': '^IXIC', 'dow jones': '^DJI', 'russell 2000': '^RUT',

  // Funds
  'mutual funds': 'VTSAX', 'global fund': 'VT', 'bond fund': 'BND'
};

// Default symbol mapping by market type & location (existing logic)
function getDefaultSymbol(marketType, country) {
  const defaults = {
    stock: { 'US': 'AAPL', 'IN': 'RELIANCE.NS', 'JP': '6758.T', 'default': 'AAPL' },
    crypto: { 'US': 'BTC-USD', 'IN': 'BTC-INR', 'default': 'BTC-USD' },
    forex: { 'US': 'EURUSD=X', 'JP': 'USDJPY=X', 'default': 'EURUSD=X' },
    commodity: { 'default': 'GC=F' }
  };
  return defaults[marketType]?.[country] || defaults[marketType]?.['default'] || 'AAPL';
}

let normalizedMarket = marketTypes[market] || 'stock';
let symbol = themeSymbols[market] || getDefaultSymbol(normalizedMarket, location);

// Date range calculation
const endDate = new Date();
const startDate = new Date();
if (timeRange === '1W') startDate.setDate(endDate.getDate() - 7);
if (timeRange === '1M') startDate.setMonth(endDate.getMonth() - 1);
if (timeRange === '3M') startDate.setMonth(endDate.getMonth() - 3);
if (timeRange === '6M') startDate.setMonth(endDate.getMonth() - 6);
if (timeRange === '1Y') startDate.setFullYear(endDate.getFullYear() - 1);
if (timeRange === '2Y') startDate.setFullYear(endDate.getFullYear() - 2);

return [{
  json: {
    originalInput: inputData,
    market: normalizedMarket,
    location,
    timeRange,
    symbol,
    fromDate: Math.floor(startDate.getTime() / 1000),
    toDate: Math.floor(endDate.getTime() / 1000),
    fromDateString: startDate.toISOString().split('T')[0],
    toDateString: endDate.toISOString().split('T')[0],
    timestamp: new Date().toISOString(),
    debug: { normalizedMarket, location, symbol }
  }
}];

// file: n8n/advanced-market-analysis-engine.js
// Purpose: Merge & summarize multiple HTTP API responses (Finnhub, Yahoo, Marketstack, AlphaVantage, Polygon)
// Behavior: Dynamic fallback. Works even if some sources fail. Emits numeric stats + short textual analysis.

// ---- Helpers ----
function safeItems(name) {
  try {
    return $items(name, 0, $runIndex) || [];
  } catch (_) {
    return [];
  }
}

function takeJsonArray(items) {
  return (items || []).map(i => (i.json ?? i)).filter(Boolean);
}

function hasErrorPayload(obj) {
  const j = obj?.json ?? obj;
  return !!(j?.error && typeof j.error === 'object');
}

function normalizeData(apiResponse, source) {
  const out = [];
  try {
    switch (source) {
      case 'finnhub': {
        const r = apiResponse;
        if (Array.isArray(r?.c) && Array.isArray(r?.t)) {
          for (let i = 0; i < r.c.length; i++) {
            if (r.c[i] == null) continue;
            out.push({
              date: new Date(r.t[i] * 1000).toISOString().slice(0, 10),
              open: r.o?.[i] ?? r.c[i],
              high: r.h?.[i] ?? r.c[i],
              low: r.l?.[i] ?? r.c[i],
              close: r.c[i],
              volume: r.v?.[i] ?? 0,
            });
          }
        }
        break;
      }
      case 'yahoo': {
        const r = apiResponse?.chart?.result?.[0];
        const ts = r?.timestamp || [];
        const q = r?.indicators?.quote?.[0] || {};
        for (let i = 0; i < ts.length; i++) {
          const close = q.close?.[i];
          if (close == null) continue;
          out.push({
            date: new Date(ts[i] * 1000).toISOString().slice(0, 10),
            open: q.open?.[i] ?? close,
            high: q.high?.[i] ?? close,
            low: q.low?.[i] ?? close,
            close,
            volume: q.volume?.[i] ?? 0,
          });
        }
        break;
      }
      case 'marketstack': {
        const arr = apiResponse?.data;
        if (Array.isArray(arr)) {
          for (const d of arr) {
            if (d?.close == null) continue;
            out.push({
              date: String(d.date).slice(0, 10),
              open: d.open ?? d.close,
              high: d.high ?? d.close,
              low: d.low ?? d.close,
              close: d.close,
              volume: d.volume ?? 0,
            });
          }
        }
        break;
      }
      case 'alpha': {
        const tsd = apiResponse?.['Time Series (Daily)'];
        if (tsd && typeof tsd === 'object') {
          for (const [date, d] of Object.entries(tsd)) {
            const close = parseFloat(d['4. close']);
            if (Number.isNaN(close)) continue;
            out.push({
              date,
              open: parseFloat(d['1. open']) || close,
              high: parseFloat(d['2. high']) || close,
              low: parseFloat(d['3. low']) || close,
              close,
              volume: parseInt(d['5. volume']) || 0,
            });
          }
        }
        break;
      }
      case 'polygon': {
        const arr = apiResponse?.results;
        if (Array.isArray(arr)) {
          for (const d of arr) {
            if (d?.c == null) continue;
            out.push({
              date: new Date(d.t).toISOString().slice(0, 10),
              open: d.o ?? d.c,
              high: d.h ?? d.c,
              low: d.l ?? d.c,
              close: d.c,
              volume: d.v ?? 0,
            });
          }
        }
        break;
      }
    }
  } catch (e) {
    // Only warn on unexpected shapes
    console.log(`normalizeData(${source}) failed:`, e.message);
  }
  return out;
}

function avg(nums) {
  const arr = nums.filter(n => Number.isFinite(n));
  return arr.length ? arr.reduce((a, b) => a + b, 0) / arr.length : 0;
}

function stdDev(nums) {
  const arr = nums.filter(n => Number.isFinite(n));
  if (arr.length < 2) return 0;
  const m = avg(arr);
  const v = avg(arr.map(x => (x - m) ** 2));
  return Math.sqrt(v);
}

function pickSymbol({ yahooMeta, marketstackFirst, alphaMeta, polygonTop, fallback }) {
  return (
    yahooMeta?.symbol ||
    marketstackFirst?.symbol ||
    alphaMeta?.symbol ||
    polygonTop?.ticker ||
    fallback ||
    'UNKNOWN'
  );
}

// ---- Collect node outputs explicitly (dynamic fallback) ----
const finnhubItems = takeJsonArray(safeItems('1. Try Finnhub API'));
const yahooItems = takeJsonArray(safeItems('2. Try Yahoo Finance API'));
const marketstackItems = takeJsonArray(safeItems('3. Try Marketstack API'));
const alphaItems = takeJsonArray(safeItems('4. Try Alpha Vantage API'));
const polygonItems = takeJsonArray(safeItems('5. Try Polygon API'));
const validateItems = takeJsonArray(safeItems('Validate & Parse Input'));

// Recover original request if present
let original = validateItems?.[0]?.json ?? validateItems?.[0] ?? {};

// Track errors for transparency
const errorSources = [];
for (const [name, arr] of Object.entries({
  finnhub: finnhubItems,
  yahoo: yahooItems,
  marketstack: marketstackItems,
  alpha: alphaItems,
  polygon: polygonItems,
})) {
  if (arr.some(hasErrorPayload)) errorSources.push(name);
}

// Normalize each source
const normalized = [];
let sourcesUsed = [];

if (finnhubItems.length && !finnhubItems.some(hasErrorPayload)) {
  normalized.push(...normalizeData(finnhubItems?.[0], 'finnhub'));
  if (normalized.length) sourcesUsed.push('finnhub');
}
if (yahooItems.length && !yahooItems.some(hasErrorPayload)) {
  normalized.push(...normalizeData(yahooItems?.[0], 'yahoo'));
  if (normalized.length) sourcesUsed.push('yahoo');
}
if (marketstackItems.length && !marketstackItems.some(hasErrorPayload)) {
  normalized.push(...normalizeData(marketstackItems?.[0], 'marketstack'));
  if (normalized.length) sourcesUsed.push('marketstack');
}
if (alphaItems.length && !alphaItems.some(hasErrorPayload)) {
  normalized.push(...normalizeData(alphaItems?.[0], 'alpha'));
  if (normalized.length) sourcesUsed.push('alpha');
}
if (polygonItems.length && !polygonItems.some(hasErrorPayload)) {
  normalized.push(...normalizeData(polygonItems?.[0], 'polygon'));
  if (normalized.length) sourcesUsed.push('polygon');
}

if (!normalized.length) {
  return [{ json: { error: true, message: 'No valid data from any API', errorSources } }];
}

// Merge by date (per-date average) and sort
const byDate = new Map();
for (const p of normalized) {
  const d = p.date;
  const entry = byDate.get(d) || { opens: [], highs: [], lows: [], closes: [], volume: 0 };
  entry.opens.push(p.open);
  entry.highs.push(p.high);
  entry.lows.push(p.low);
  entry.closes.push(p.close);
  entry.volume += Number(p.volume) || 0;
  byDate.set(d, entry);
}

const dates = Array.from(byDate.keys()).sort((a, b) => new Date(a) - new Date(b));
const prices = dates.map(d => {
  const e = byDate.get(d);
  return {
    date: d,
    open: +avg(e.opens).toFixed(4),
    high: Math.max(...e.highs.filter(Number.isFinite)),
    low: Math.min(...e.lows.filter(Number.isFinite)),
    close: +avg(e.closes).toFixed(4),
    volume: e.volume,
  };
});

// Derive request fallback if original missing
const yahooMeta = yahooItems?.[0]?.chart?.result?.[0]?.meta;
const marketstackFirst = marketstackItems?.[0]?.data?.[0];
const alphaMetaRaw = alphaItems?.[0]?.['Meta Data'];
const alphaMeta = alphaMetaRaw ? { symbol: alphaMetaRaw['2. Symbol'] } : undefined;
const polygonTop = polygonItems?.[0];

const derivedSymbol = pickSymbol({ yahooMeta, marketstackFirst, alphaMeta, polygonTop, fallback: original?.symbol });
const fromDateString = prices[0].date;
const toDateString = prices[prices.length - 1].date;

if (!original || !original.market) {
  original = {
    ...(original || {}),
    symbol: derivedSymbol,
    fromDateString,
    toDateString,
  };
}

// Stats
const firstClose = prices[0].close;
const lastClose = prices[prices.length - 1].close;
const change = +(lastClose - firstClose).toFixed(4);
const pctChange = +(firstClose ? (change / firstClose) * 100 : 0).toFixed(2);
const high = Math.max(...prices.map(p => p.high));
const low = Math.min(...prices.map(p => p.low));
const avgClose = +avg(prices.map(p => p.close)).toFixed(4);
const volStd = +stdDev(prices.map(p => p.close)).toFixed(4);

// Textual analysis (brief & actionable)
let trend = 'sideways movement';
if (pctChange > 5) trend = 'strong bullish trend';
else if (pctChange > 0) trend = 'mild upward trend';
else if (pctChange < -5) trend = 'bearish trend';

const volatility = volStd > avgClose * 0.02 ? 'elevated volatility' : 'stable volatility';
const analysisText = `From ${fromDateString} to ${toDateString}, ${derivedSymbol} shows a ${trend} (${pctChange}% change). Range ${low.toFixed(2)}â€“${high.toFixed(2)} with ${volatility}.`;

// Emit single item for webhook
return [{
  json: {
    request: original,
    summary: {
      firstClose,
      lastClose,
      change,
      pctChange,
      high,
      low,
      avgClose,
      volatilityStd: volStd,
      dataPoints: prices.length,
      sourcesUsed: Array.from(new Set(sourcesUsed)),
      errorSources,
      aggregation: 'per-date average',
    },
    analysis: analysisText,
    prices,
  }
}];
