// Unified activity processing for all engagement types
const activityData = $input.all()[0].json;
const triggerNode = Object.keys($workflow.active)[0];

// Enhanced validation with detailed error information
const validationErrors = [];

if (!activityData.email && !activityData.user_id) {
  validationErrors.push('Missing required identifier: email or user_id');
}

if (!activityData.timestamp) {
  activityData.timestamp = new Date().toISOString();
}

if (activityData.email && !/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(activityData.email)) {
  validationErrors.push('Invalid email format');
}

if (validationErrors.length > 0) {
  return { json: { 
    validation_errors: validationErrors,
    skip_processing: true,
    raw_data: activityData,
    error_type: 'validation_failed'
  }};
}

// Determine activity source and type
let activitySource = 'unknown';
let activityType = 'general';
let baseScore = 0;
let engagementLevel = 'low';

// Map trigger nodes to activity sources
if (triggerNode.includes('website')) {
  activitySource = 'website';
  activityType = activityData.activity_type || 'page_view';
} else if (triggerNode.includes('email')) {
  activitySource = 'email';
  activityType = activityData.event_type || 'opened';
} else if (triggerNode.includes('social')) {
  activitySource = 'social_media';
  activityType = activityData.interaction_type || 'engagement';
} else if (triggerNode.includes('content')) {
  activitySource = 'content';
  activityType = activityData.content_type || 'download';
} else if (triggerNode.includes('sales')) {
  activitySource = 'sales';
  activityType = activityData.interaction_type || 'call';
}

// Advanced scoring matrix based on activity type and context
const scoringMatrix = {
  website: {
    'page_view': { base: 1, multiplier: 1.0, decay: 0.95 },
    'pricing_page': { base: 15, multiplier: 1.5, decay: 0.9 },
    'demo_page': { base: 25, multiplier: 2.0, decay: 0.85 },
    'contact_page': { base: 20, multiplier: 1.8, decay: 0.9 },
    'features_page': { base: 8, multiplier: 1.2, decay: 0.92 },
    'case_studies': { base: 12, multiplier: 1.4, decay: 0.9 },
    'blog_post': { base: 3, multiplier: 1.1, decay: 0.95 },
    'documentation': { base: 10, multiplier: 1.3, decay: 0.9 },
    'calculator_tool': { base: 18, multiplier: 1.7, decay: 0.88 },
    'roi_calculator': { base: 22, multiplier: 1.9, decay: 0.85 },
    'search_internal': { base: 5, multiplier: 1.1, decay: 0.93 }
  },
  email: {
    'opened': { base: 5, multiplier: 1.0, decay: 0.9 },
    'clicked': { base: 15, multiplier: 1.5, decay: 0.85 },
    'replied': { base: 30, multiplier: 2.5, decay: 0.8 },
    'forwarded': { base: 25, multiplier: 2.0, decay: 0.85 },
    'unsubscribed': { base: -50, multiplier: 1.0, decay: 1.0 },
    'spam_reported': { base: -75, multiplier: 1.0, decay: 1.0 },
    'downloaded_attachment': { base: 20, multiplier: 1.8, decay: 0.88 },
    'calendar_booking': { base: 40, multiplier: 3.0, decay: 0.75 }
  },
  social_media: {
    'like': { base: 2, multiplier: 1.0, decay: 0.95 },
    'share': { base: 8, multiplier: 1.4, decay: 0.9 },
    'comment': { base: 12, multiplier: 1.6, decay: 0.88 },
    'mention': { base: 15, multiplier: 1.8, decay: 0.85 },
    'dm_sent': { base: 25, multiplier: 2.2, decay: 0.8 },
    'connection_request': { base: 18, multiplier: 1.9, decay: 0.85 },
    'profile_visit': { base: 5, multiplier: 1.2, decay: 0.92 },
    'company_follow': { base: 10, multiplier: 1.5, decay: 0.9 },
    'event_rsvp': { base: 30, multiplier: 2.5, decay: 0.8 }
  },
  content: {
    'whitepaper_download': { base: 20, multiplier: 1.8, decay: 0.85 },
    'ebook_download': { base: 18, multiplier: 1.7, decay: 0.87 },
    'case_study_download': { base: 22, multiplier: 1.9, decay: 0.83 },
    'webinar_registration': { base: 25, multiplier: 2.1, decay: 0.8 },
    'webinar_attendance': { base: 35, multiplier: 2.8, decay: 0.75 },
    'trial_signup': { base: 50, multiplier: 3.5, decay: 0.7 },
    'demo_request': { base: 45, multiplier: 3.2, decay: 0.72 },
    'newsletter_signup': { base: 8, multiplier: 1.3, decay: 0.9 },
    'video_watched': { base: 6, multiplier: 1.2, decay: 0.92 },
    'podcast_listened': { base: 12, multiplier: 1.5, decay: 0.88 }
  },
  sales: {
    'call_scheduled': { base: 40, multiplier: 3.0, decay: 0.75 },
    'call_completed': { base: 50, multiplier: 3.5, decay: 0.7 },
    'call_no_show': { base: -15, multiplier: 1.0, decay: 0.95 },
    'proposal_sent': { base: 35, multiplier: 2.8, decay: 0.78 },
    'proposal_viewed': { base: 25, multiplier: 2.2, decay: 0.82 },
    'contract_sent': { base: 60, multiplier: 4.0, decay: 0.65 },
    'contract_viewed': { base: 45, multiplier: 3.3, decay: 0.7 },
    'meeting_attended': { base: 30, multiplier: 2.5, decay: 0.8 },
    'objection_raised': { base: 10, multiplier: 1.4, decay: 0.9 },
    'referral_given': { base: 55, multiplier: 3.8, decay: 0.68 }
  }
};

// Get scoring parameters
const scoring = scoringMatrix[activitySource]?.[activityType] || { base: 1, multiplier: 1.0, decay: 0.95 };
baseScore = scoring.base;

// Calculate contextual multipliers
let contextMultiplier = 1.0;
let sessionMultiplier = 1.0;
let frequencyMultiplier = 1.0;
let timeMultiplier = 1.0;
let deviceMultiplier = 1.0;

// Context-based scoring adjustments
if (activityData.context) {
  // Time-based multipliers
  const hour = new Date().getHours();
  if (hour >= 9 && hour <= 17) timeMultiplier = 1.2; // Business hours
  if (hour >= 18 && hour <= 22) timeMultiplier = 1.1; // Evening engagement

  // Device-based multipliers
  if (activityData.context.device_type === 'mobile') deviceMultiplier = 1.1;
  if (activityData.context.device_type === 'desktop') deviceMultiplier = 1.15;

  // Geographic multipliers
  if (activityData.context.country && activityData.context.country === 'US') contextMultiplier *= 1.1;
  if (activityData.context.is_high_value_location) contextMultiplier *= 1.15;

  // Company size multipliers
  if (activityData.context.company_size) {
    switch(activityData.context.company_size) {
      case 'enterprise': contextMultiplier *= 1.5; break;
      case 'mid_market': contextMultiplier *= 1.3; break;
      case 'smb': contextMultiplier *= 1.1; break;
    }
  }

  // Industry multipliers
  const highValueIndustries = ['technology', 'finance', 'healthcare', 'manufacturing'];
  if (activityData.context.industry && highValueIndustries.includes(activityData.context.industry)) {
    contextMultiplier *= 1.2;
  }

  // Session depth multiplier (website activities)
  if (activityData.context.session_depth) {
    sessionMultiplier = Math.min(2.0, 1 + (activityData.context.session_depth - 1) * 0.1);
  }

  // Time on page/content multiplier
  if (activityData.context.time_spent) {
    const timeSpent = parseInt(activityData.context.time_spent);
    if (timeSpent > 120) timeMultiplier *= 1.3; // 2+ minutes
    else if (timeSpent > 60) timeMultiplier *= 1.15; // 1+ minute
    else if (timeSpent < 10) timeMultiplier *= 0.8; // Quick bounce
  }
}

// Frequency-based scoring (diminishing returns)
if (activityData.frequency_count) {
  const frequency = parseInt(activityData.frequency_count);
  frequencyMultiplier = Math.max(0.3, 1 - (frequency - 1) * 0.1);
}

// Calculate final score
const totalMultiplier = contextMultiplier * sessionMultiplier * frequencyMultiplier * timeMultiplier * deviceMultiplier;
const calculatedScore = Math.round(baseScore * scoring.multiplier * totalMultiplier);

// Determine engagement level
if (calculatedScore >= 30) engagementLevel = 'high';
else if (calculatedScore >= 15) engagementLevel = 'medium';
else if (calculatedScore >= 5) engagementLevel = 'low';
else if (calculatedScore < 0) engagementLevel = 'negative';

// Prepare processed activity data
const processedActivity = {
  // Core identification
  contact_identifier: activityData.email || activityData.user_id,
  contact_email: activityData.email,
  contact_id: activityData.contact_id || null,

  // Activity details
  activity_source: activitySource,
  activity_type: activityType,
  activity_timestamp: activityData.timestamp || new Date().toISOString(),

  // Scoring details
  base_score: baseScore,
  calculated_score: calculatedScore,
  engagement_level: engagementLevel,

  // Multiplier breakdown for transparency
  scoring_breakdown: {
    base_score: baseScore,
    base_multiplier: scoring.multiplier,
    context_multiplier: contextMultiplier,
    session_multiplier: sessionMultiplier,
    frequency_multiplier: frequencyMultiplier,
    time_multiplier: timeMultiplier,
    device_multiplier: deviceMultiplier,
    total_multiplier: totalMultiplier
  },

  // Context data
  context: {
    device_type: activityData.context?.device_type || 'unknown',
    location: activityData.context?.location || {},
    session_id: activityData.context?.session_id,
    page_url: activityData.context?.page_url,
    referrer: activityData.context?.referrer,
    utm_source: activityData.context?.utm_source,
    utm_medium: activityData.context?.utm_medium,
    utm_campaign: activityData.context?.utm_campaign,
    company_size: activityData.context?.company_size,
    industry: activityData.context?.industry,
    time_spent: activityData.context?.time_spent,
    session_depth: activityData.context?.session_depth
  },

  // Additional metadata
  metadata: {
    trigger_source: triggerNode,
    processing_timestamp: new Date().toISOString(),
    workflow_version: '1.0',
    decay_factor: scoring.decay
  },

  // Raw activity data for debugging
  raw_activity_data: activityData
};

return { json: processedActivity };

// Advanced behavioral score calculation with machine learning-like features
const activityData = $node["Valid Activity?"].json;
const contactData = $node["Get Contact Details"].json;

if (!contactData.contact) {
  return { json: { ...activityData, skip: true, reason: 'Contact not found in CRM' } };
}

const contact = contactData.contact;
const recentActivities = contactData.recent_activities || [];
const scoringHistory = contactData.scoring_history || [];

// Current behavioral score
const currentScore = contact.behavioral_score || 0;
const currentTemperature = contact.lead_temperature || 'cold';

// Calculate recency and frequency factors
const now = new Date();
const thirtyDaysAgo = new Date(now - 30 * 24 * 60 * 60 * 1000);
const sevenDaysAgo = new Date(now - 7 * 24 * 60 * 60 * 1000);
const oneDayAgo = new Date(now - 1 * 24 * 60 * 60 * 1000);

// Activity frequency analysis
const recentActivityCounts = {
  last_24h: recentActivities.filter(a => new Date(a.created_at) > oneDayAgo).length,
  last_7d: recentActivities.filter(a => new Date(a.created_at) > sevenDaysAgo).length,
  last_30d: recentActivities.filter(a => new Date(a.created_at) > thirtyDaysAgo).length,
  same_type_30d: recentActivities.filter(a => 
    a.type === activityData.activity_type && new Date(a.created_at) > thirtyDaysAgo
  ).length
};

// Engagement pattern analysis
const engagementPatterns = {
  peak_activity_day: null,
  peak_activity_hour: null,
  engagement_consistency: 0,
  progressive_engagement: 0,
  multi_channel_engagement: 0
};

// Analyze daily patterns
const dailyActivity = {};
const hourlyActivity = {};
recentActivities.forEach(activity => {
  const date = new Date(activity.created_at);
  const day = date.getDay();
  const hour = date.getHours();

  dailyActivity[day] = (dailyActivity[day] || 0) + 1;
  hourlyActivity[hour] = (hourlyActivity[hour] || 0) + 1;
});

engagementPatterns.peak_activity_day = Object.keys(dailyActivity).reduce((a, b) => 
  dailyActivity[a] > dailyActivity[b] ? a : b, '0'
);

engagementPatterns.peak_activity_hour = Object.keys(hourlyActivity).reduce((a, b) => 
  hourlyActivity[a] > hourlyActivity[b] ? a : b, '0'
);

// Calculate engagement consistency (how regularly they engage)
const uniqueDays = new Set(recentActivities.map(a => new Date(a.created_at).toDateString())).size;
engagementPatterns.engagement_consistency = uniqueDays / 30; // 0-1 score

// Calculate progressive engagement (are they moving through the funnel?)
const funnelStages = ['awareness', 'interest', 'consideration', 'intent', 'evaluation', 'purchase'];
const stageActivities = {
  awareness: ['blog_post', 'social_engagement', 'newsletter_signup'],
  interest: ['whitepaper_download', 'video_watched', 'features_page'],
  consideration: ['case_study_download', 'webinar_registration', 'comparison_page'],
  intent: ['pricing_page', 'demo_request', 'contact_page'],
  evaluation: ['trial_signup', 'proposal_viewed', 'call_scheduled'],
  purchase: ['contract_viewed', 'payment_page', 'onboarding_started']
};

const currentStage = getCurrentFunnelStage(recentActivities, stageActivities);
const previousStage = getPreviousFunnelStage(recentActivities, stageActivities);
engagementPatterns.progressive_engagement = currentStage > previousStage ? 1 : 0;

// Multi-channel engagement score
const uniqueChannels = new Set(recentActivities.map(a => a.source || 'unknown')).size;
engagementPatterns.multi_channel_engagement = Math.min(uniqueChannels / 5, 1); // 0-1 score

// Advanced scoring factors
const scoringFactors = {
  base_score: activityData.calculated_score,

  // Frequency penalties/bonuses
  frequency_factor: calculateFrequencyFactor(recentActivityCounts, activityData.activity_type),

  // Recency bonus (more recent = higher score)
  recency_factor: calculateRecencyFactor(activityData.activity_timestamp),

  // Progressive engagement bonus
  progression_bonus: engagementPatterns.progressive_engagement * 10,

  // Multi-channel bonus
  multi_channel_bonus: engagementPatterns.multi_channel_engagement * 15,

  // Consistency bonus
  consistency_bonus: engagementPatterns.engagement_consistency * 12,

  // Peak time alignment bonus
  peak_time_bonus: calculatePeakTimeBonus(activityData.activity_timestamp, engagementPatterns),

  // Velocity bonus (increasing activity frequency)
  velocity_bonus: calculateVelocityBonus(recentActivityCounts),

  // Intent signal multiplier
  intent_multiplier: calculateIntentMultiplier(activityData.activity_type, activityData.activity_source),

  // Company profile multiplier
  company_multiplier: calculateCompanyMultiplier(contact, activityData.context)
};

// Calculate frequency factor
function calculateFrequencyFactor(counts, activityType) {
  const sameTypeCount = counts.same_type_30d;

  // Different activity types have different optimal frequencies
  const optimalFrequencies = {
    'page_view': 10,
    'email_opened': 5,
    'content_download': 2,
    'demo_request': 1,
    'pricing_page': 3
  };

  const optimal = optimalFrequencies[activityType] || 5;

  if (sameTypeCount === 0) return 1.0;
  if (sameTypeCount <= optimal) return 1 + (sameTypeCount / optimal) * 0.2;
  return Math.max(0.5, 1.2 - (sameTypeCount - optimal) * 0.1); // Diminishing returns
}

// Calculate recency factor
function calculateRecencyFactor(timestamp) {
  const activityTime = new Date(timestamp);
  const hoursAgo = (now - activityTime) / (1000 * 60 * 60);

  if (hoursAgo < 1) return 1.5; // Very recent
  if (hoursAgo < 24) return 1.3; // Same day
  if (hoursAgo < 48) return 1.1; // Yesterday
  if (hoursAgo < 168) return 1.0; // This week
  return Math.max(0.8, 1 - (hoursAgo / (24 * 30)) * 0.2); // Decay over month
}

// Calculate peak time bonus
function calculatePeakTimeBonus(timestamp, patterns) {
  const activityTime = new Date(timestamp);
  const hour = activityTime.getHours();
  const day = activityTime.getDay();

  let bonus = 0;
  if (hour.toString() === patterns.peak_activity_hour) bonus += 3;
  if (day.toString() === patterns.peak_activity_day) bonus += 2;

  return bonus;
}

// Calculate velocity bonus
function calculateVelocityBonus(counts) {
  const velocity24h = counts.last_24h;
  const velocity7d = counts.last_7d / 7;
  const velocity30d = counts.last_30d / 30;

  if (velocity24h > velocity7d && velocity7d > velocity30d) {
    return 20; // Accelerating engagement
  }
  if (velocity24h > velocity7d) {
    return 10; // Recent uptick
  }
  if (velocity24h < velocity7d && velocity7d < velocity30d) {
    return -5; // Declining engagement
  }
  return 0;
}

// Calculate intent multiplier
function calculateIntentMultiplier(activityType, activitySource) {
  const intentSignals = {
    'pricing_page': 2.0,
    'demo_request': 2.5,
    'trial_signup': 3.0,
    'contact_page': 1.8,
    'proposal_viewed': 2.2,
    'contract_viewed': 3.5,
    'call_scheduled': 2.8,
    'roi_calculator': 2.1
  };

  return intentSignals[activityType] || 1.0;
}

// Calculate company multiplier
function calculateCompanyMultiplier(contact, context) {
  let multiplier = 1.0;

  // Company size multiplier
  if (contact.company_size === 'enterprise') multiplier *= 1.5;
  else if (contact.company_size === 'mid_market') multiplier *= 1.3;

  // Revenue potential multiplier
  if (contact.annual_revenue > 100000000) multiplier *= 1.4; // $100M+
  else if (contact.annual_revenue > 10000000) multiplier *= 1.2; // $10M+

  // Industry multiplier
  const highValueIndustries = ['technology', 'finance', 'healthcare', 'manufacturing'];
  if (highValueIndustries.includes(contact.industry)) multiplier *= 1.2;

  // Geographic multiplier
  if (context.location?.country === 'US') multiplier *= 1.1;

  return multiplier;
}

// Get current funnel stage
function getCurrentFunnelStage(activities, stageActivities) {
  const recentActivities = activities.filter(a => new Date(a.created_at) > sevenDaysAgo);

  for (let i = funnelStages.length - 1; i >= 0; i--) {
    const stage = funnelStages[i];
    const stageTypes = stageActivities[stage];

    if (recentActivities.some(a => stageTypes.includes(a.type))) {
      return i;
    }
  }
  return 0;
}

// Get previous funnel stage
function getPreviousFunnelStage(activities, stageActivities) {
  const olderActivities = activities.filter(a => {
    const date = new Date(a.created_at);
    return date <= sevenDaysAgo && date > thirtyDaysAgo;
  });

  for (let i = funnelStages.length - 1; i >= 0; i--) {
    const stage = funnelStages[i];
    const stageTypes = stageActivities[stage];

    if (olderActivities.some(a => stageTypes.includes(a.type))) {
      return i;
    }
  }
  return 0;
}

// Calculate final score
const totalScoreAdjustment = Math.round(
  scoringFactors.base_score * 
  scoringFactors.frequency_factor * 
  scoringFactors.recency_factor * 
  scoringFactors.intent_multiplier * 
  scoringFactors.company_multiplier +
  scoringFactors.progression_bonus +
  scoringFactors.multi_channel_bonus +
  scoringFactors.consistency_bonus +
  scoringFactors.peak_time_bonus +
  scoringFactors.velocity_bonus
);

const newBehavioralScore = Math.max(0, Math.min(100, currentScore + totalScoreAdjustment));

// Determine new lead temperature
let newTemperature = 'cold';
if (newBehavioralScore >= 75) newTemperature = 'hot';
else if (newBehavioralScore >= 50) newTemperature = 'warm';
else if (newBehavioralScore >= 25) newTemperature = 'cool';

// Detect significant changes
const significantChange = Math.abs(totalScoreAdjustment) >= 10;
const temperatureChanged = newTemperature !== currentTemperature;
const majorIncrease = totalScoreAdjustment >= 20;
const majorDecrease = totalScoreAdjustment <= -15;

const result = {
  // Contact information
  contact_id: contact._id,
  contact_email: contact.email,
  contact_name: `${contact.first_name} ${contact.last_name}`,
  company: contact.company,

  // Scoring details
  previous_score: currentScore,
  score_adjustment: totalScoreAdjustment,
  new_behavioral_score: newBehavioralScore,
  previous_temperature: currentTemperature,
  new_temperature: newTemperature,

  // Change indicators
  significant_change: significantChange,
  temperature_changed: temperatureChanged,
  major_increase: majorIncrease,
  major_decrease: majorDecrease,

  // Detailed scoring breakdown
  scoring_factors: scoringFactors,
  engagement_patterns: engagementPatterns,
  activity_frequency: recentActivityCounts,

  // Activity data
  triggering_activity: {
    type: activityData.activity_type,
    source: activityData.activity_source,
    timestamp: activityData.activity_timestamp,
    engagement_level: activityData.engagement_level
  },

  // Insights and recommendations
  insights: generateInsights(scoringFactors, engagementPatterns, recentActivityCounts),

  // Processing metadata
  processing_timestamp: new Date().toISOString(),
  workflow_version: '2.0'
};

// Generate insights
function generateInsights(factors, patterns, counts) {
  const insights = [];

  if (factors.progression_bonus > 0) {
    insights.push('Contact is progressing through the sales funnel');
  }

  if (factors.multi_channel_bonus > 10) {
    insights.push('High multi-channel engagement indicates strong interest');
  }

  if (factors.velocity_bonus > 15) {
    insights.push('Accelerating engagement pattern - prime for outreach');
  }

  if (factors.consistency_bonus > 8) {
    insights.push('Consistent engagement pattern indicates sustained interest');
  }

  if (counts.last_24h > 5) {
    insights.push('High activity in last 24 hours - consider immediate follow-up');
  }

  if (factors.intent_multiplier > 2.0) {
    insights.push('High-intent activity detected - sales-ready signal');
  }

  return insights;
}

return { json: result };

