// Process score decay for inactive contacts
const decayData = $input.all()[0].json;

if (!decayData.contacts || !Array.isArray(decayData.contacts)) {
  return [];
}

const processedContacts = decayData.contacts.map(contact => {
  const currentScore = contact.behavioral_score || 0;
  const daysSinceActivity = contact.days_since_last_activity || 0;
  const currentTemperature = contact.lead_temperature || 'cold';

  // Calculate decay amount based on inactivity period
  let decayRate = 0.02; // Base decay rate (2% per week)

  // Accelerated decay for longer inactivity
  if (daysSinceActivity > 30) decayRate = 0.05; // 5% per week after 30 days
  else if (daysSinceActivity > 14) decayRate = 0.03; // 3% per week after 14 days

  // Calculate decay amount (weekly decay applied proportionally)
  const weeksInactive = daysSinceActivity / 7;
  const decayMultiplier = Math.pow(1 - decayRate, weeksInactive);
  const newScore = Math.max(0, Math.round(currentScore * decayMultiplier));
  const scoreDecay = currentScore - newScore;

  // Determine new temperature
  let newTemperature = 'cold';
  if (newScore >= 75) newTemperature = 'hot';
  else if (newScore >= 50) newTemperature = 'warm';
  else if (newScore >= 25) newTemperature = 'cool';

  return {
    contact_id: contact._id,
    contact_email: contact.email,
    contact_name: `${contact.first_name} ${contact.last_name}`,
    company: contact.company,
    previous_score: currentScore,
    new_score: newScore,
    score_decay: scoreDecay,
    previous_temperature: currentTemperature,
    new_temperature: newTemperature,
    temperature_changed: currentTemperature !== newTemperature,
    days_inactive: daysSinceActivity,
    decay_rate_applied: decayRate,
    last_activity_type: contact.last_activity_type,
    last_activity_date: contact.last_activity_date,
    processing_timestamp: new Date().toISOString()
  };
});

// Filter out contacts with no significant change
const significantChanges = processedContacts.filter(contact => 
  contact.score_decay >= 1 || contact.temperature_changed
);

return significantChanges.map(contact => ({ json: contact }));

