// Route client events to appropriate communication workflows
const eventData = $input.all()[0].json;
const clientData = $input.first().json.data;

const eventType = eventData.eventType;
const clientInfo = {
  id: clientData.id,
  companyName: clientData.company_name,
  industry: clientData.industry,
  status: clientData.status,
  createdAt: clientData.created_at,
  contacts: clientData.client_contacts || [],
  primaryContact: clientData.client_contacts?.find(c => c.is_primary) || clientData.client_contacts?.[0]
};

// Calculate client lifecycle stage
const daysSinceOnboarding = Math.floor((Date.now() - new Date(clientData.created_at).getTime()) / (1000 * 60 * 60 * 24));
let lifecycleStage = 'new';

if (daysSinceOnboarding > 90) lifecycleStage = 'established';
else if (daysSinceOnboarding > 30) lifecycleStage = 'growing';
else if (daysSinceOnboarding > 7) lifecycleStage = 'onboarding';

// Determine communication workflow based on event type
let workflowType = '';
let urgency = 'normal';
let scheduledDelay = 0; // minutes

switch (eventType) {
  case 'client_onboarded':
    workflowType = 'welcome_sequence';
    urgency = 'high';
    scheduledDelay = 30; // 30 minutes after onboarding
    break;

  case 'project_completed':
    workflowType = 'project_completion';
    urgency = 'high';
    scheduledDelay = 60; // 1 hour after completion
    break;

  case 'monthly_checkin':
    workflowType = 'monthly_checkin';
    urgency = 'normal';
    scheduledDelay = 0; // Immediate
    break;

  case 'payment_overdue':
    workflowType = 'payment_reminder';
    urgency = 'urgent';
    scheduledDelay = 0; // Immediate
    break;

  case 'contract_renewal_due':
    workflowType = 'renewal_reminder';
    urgency = 'high';
    scheduledDelay = 0; // Immediate
    break;

  case 'project_milestone':
    workflowType = 'milestone_update';
    urgency = 'normal';
    scheduledDelay = 120; // 2 hours after milestone
    break;

  case 'performance_report_ready':
    workflowType = 'report_delivery';
    urgency = 'normal';
    scheduledDelay = 15; // 15 minutes to prepare
    break;

  case 'issue_escalation':
    workflowType = 'issue_resolution';
    urgency = 'urgent';
    scheduledDelay = 0; // Immediate
    break;

  case 'satisfaction_survey':
    workflowType = 'feedback_collection';
    urgency = 'normal';
    scheduledDelay = 24 * 60; // 24 hours delay
    break;

  default:
    workflowType = 'general_update';
    urgency = 'normal';
    scheduledDelay = 60;
}

// Check for communication preferences
const communicationChannels = ['email'];
if (clientInfo.primaryContact?.phone) communicationChannels.push('sms');
if (eventData.urgency === 'urgent') communicationChannels.push('slack');

// Calculate send time (business hours optimization)
const sendTime = new Date(Date.now() + scheduledDelay * 60000);
const sendHour = sendTime.getHours();
const sendDay = sendTime.getDay();

// Adjust for business hours (9 AM - 6 PM, Monday-Friday)
if (sendHour < 9 || sendHour > 18 || sendDay === 0 || sendDay === 6) {
  const nextBusinessDay = new Date(sendTime);

  // Move to next business day
  while (nextBusinessDay.getDay() === 0 || nextBusinessDay.getDay() === 6) {
    nextBusinessDay.setDate(nextBusinessDay.getDate() + 1);
  }

  // Set to 9 AM
  nextBusinessDay.setHours(9, 0, 0, 0);
  sendTime.setTime(nextBusinessDay.getTime());
}

return [{
  json: {
    eventData,
    clientInfo,
    workflowType,
    urgency,
    lifecycleStage,
    communicationChannels,
    sendTime: sendTime.toISOString(),
    scheduledDelay,
    isImmediate: scheduledDelay === 0 && urgency === 'urgent',
    personalizedData: {
      primaryContactName: clientInfo.primaryContact?.first_name || 'there',
      companyName: clientInfo.companyName,
      industry: clientInfo.industry,
      relationshipDuration: daysSinceOnboarding
    }
  }
}];

// Generate personalized communication content
const routingData = $input.all()[0].json;
const templatesData = $input.first().json.data;

const workflowType = routingData.workflowType;
const clientInfo = routingData.clientInfo;
const personalizedData = routingData.personalizedData;

// Find appropriate template
const template = templatesData.find(t => 
  t.name.toLowerCase().includes(workflowType.toLowerCase()) ||
  t.description?.toLowerCase().includes(workflowType.toLowerCase())
) || templatesData[0]; // Fallback to first template

// Base personalization variables
const personalizations = {
  clientName: personalizedData.primaryContactName,
  companyName: personalizedData.companyName,
  industry: personalizedData.industry,
  relationshipDuration: personalizedData.relationshipDuration,
  currentDate: new Date().toLocaleDateString(),
  agencyName: 'Your Marketing Agency' // Would come from agency settings
};

// Workflow-specific content generation
let subject = '';
let content = '';
let callToAction = '';
let attachments = [];

switch (workflowType) {
  case 'welcome_sequence':
    subject = `Welcome to ${personalizations.agencyName}, ${personalizations.clientName}!`;
    content = `Dear ${personalizations.clientName},\n\nWelcome to ${personalizations.agencyName}! We're thrilled to partner with ${personalizations.companyName} on your marketing journey.\n\nOver the next few days, you'll receive:\nâ€¢ Your dedicated account manager introduction\nâ€¢ Project timeline and milestones\nâ€¢ Access to your client portal\nâ€¢ Our proven ${personalizations.industry} marketing strategies\n\nWe're committed to delivering exceptional results for ${personalizations.companyName}.`;
    callToAction = 'Schedule Your Kick-off Call';
    break;

  case 'monthly_checkin':
    subject = `Monthly Update - ${personalizations.companyName} Performance`;
    content = `Hi ${personalizations.clientName},\n\nHope you're doing well! As part of our commitment to transparent communication, here's your monthly update for ${personalizations.companyName}.\n\nThis month's highlights:\nâ€¢ Campaign performance metrics\nâ€¢ Key achievements and wins\nâ€¢ Upcoming strategies and initiatives\nâ€¢ Any challenges and our solutions\n\nWe've been working together for ${personalizations.relationshipDuration} days and we're excited about the continued growth!`;
    callToAction = 'View Detailed Report';
    break;

  case 'project_completion':
    subject = `Project Complete - ${personalizations.companyName} Success!`;
    content = `Congratulations ${personalizations.clientName}!\n\nWe're excited to share that your latest project has been completed successfully! The ${personalizations.companyName} campaign exceeded expectations.\n\nProject Results:\nâ€¢ All deliverables completed on time\nâ€¢ Performance metrics exceeded targets\nâ€¢ ROI improvement documented\nâ€¢ Next phase recommendations prepared\n\nThank you for trusting us with ${personalizations.companyName}'s marketing success.`;
    callToAction = 'View Project Results';
    break;

  case 'payment_reminder':
    subject = `Payment Reminder - ${personalizations.companyName} Account`;
    content = `Hi ${personalizations.clientName},\n\nThis is a friendly reminder that we have an outstanding invoice for ${personalizations.companyName}'s marketing services.\n\nInvoice Details:\nâ€¢ Due date has passed\nâ€¢ Services continue as normal\nâ€¢ Quick payment options available\nâ€¢ Questions? We're here to help\n\nWe appreciate your prompt attention to this matter.`;
    callToAction = 'Pay Invoice Now';
    break;

  case 'renewal_reminder':
    subject = `Contract Renewal - ${personalizations.companyName}`;
    content = `Hi ${personalizations.clientName},\n\nYour contract with ${personalizations.agencyName} is approaching its renewal date. We've loved working with ${personalizations.companyName} over the past ${Math.floor(personalizations.relationshipDuration / 30)} months!\n\nRenewal Benefits:\nâ€¢ Continued exceptional service\nâ€¢ Locked-in current rates\nâ€¢ Priority support and features\nâ€¢ Expanded service options\n\nLet's schedule a renewal discussion to plan ${personalizations.companyName}'s continued success.`;
    callToAction = 'Schedule Renewal Call';
    break;

  case 'milestone_update':
    subject = `Milestone Achieved - ${personalizations.companyName}`;
    content = `Great news, ${personalizations.clientName}!\n\n${personalizations.companyName} has reached another important milestone in our marketing partnership.\n\nMilestone Highlights:\nâ€¢ Target objectives met or exceeded\nâ€¢ Timeline maintained perfectly\nâ€¢ Quality standards surpassed\nâ€¢ Next phase ready to launch\n\nYour success is our success, and we're proud to be part of ${personalizations.companyName}'s growth story.`;
    callToAction = 'View Milestone Report';
    break;

  case 'report_delivery':
    subject = `Performance Report Ready - ${personalizations.companyName}`;
    content = `Hi ${personalizations.clientName},\n\nYour latest performance report for ${personalizations.companyName} is ready for review!\n\nThis Report Includes:\nâ€¢ Comprehensive analytics dashboard\nâ€¢ ROI and performance metrics\nâ€¢ Actionable insights and recommendations\nâ€¢ Competitive analysis and benchmarks\n\nWe're excited to share the positive results and discuss next steps.`;
    callToAction = 'View Performance Report';
    attachments = ['performance_report.pdf'];
    break;

  case 'issue_resolution':
    subject = `URGENT: Issue Resolution - ${personalizations.companyName}`;
    content = `Hi ${personalizations.clientName},\n\nWe've identified and resolved an issue affecting ${personalizations.companyName}'s campaign. Our team took immediate action to minimize any impact.\n\nIssue Summary:\nâ€¢ Issue detected and resolved quickly\nâ€¢ Minimal impact on campaign performance\nâ€¢ Preventive measures implemented\nâ€¢ Full transparency report attached\n\nWe apologize for any inconvenience and appreciate your understanding.`;
    callToAction = 'Schedule Resolution Call';
    break;

  case 'feedback_collection':
    subject = `Your Feedback Matters - ${personalizations.companyName}`;
    content = `Hi ${personalizations.clientName},\n\nWe value your partnership with ${personalizations.companyName} and would love to hear about your experience with ${personalizations.agencyName}.\n\nYour Feedback Helps Us:\nâ€¢ Improve our service quality\nâ€¢ Better serve ${personalizations.industry} clients\nâ€¢ Enhance our partnership\nâ€¢ Deliver even better results\n\nThe survey takes just 2-3 minutes and your insights are invaluable.`;
    callToAction = 'Share Your Feedback';
    break;

  default:
    subject = `Update from ${personalizations.agencyName} - ${personalizations.companyName}`;
    content = `Hi ${personalizations.clientName},\n\nWe wanted to reach out with an important update regarding ${personalizations.companyName}'s marketing initiatives.\n\nWe're continuously working to optimize your results and ensure ${personalizations.companyName} stays ahead of the competition.\n\nThank you for being an amazing partner!`;
    callToAction = 'Contact Your Account Manager';
}

// Apply template customizations if available
if (template && template.template) {
  const templateContent = template.template;

  // Replace placeholders with personalized content
  Object.keys(personalizations).forEach(key => {
    const placeholder = new RegExp(`{{${key}}}`, 'gi');
    subject = subject.replace(placeholder, personalizations[key]);
    content = content.replace(placeholder, personalizations[key]);
    callToAction = callToAction.replace(placeholder, personalizations[key]);
  });
}

// Generate email HTML
const htmlContent = `
<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>${subject}</title>
    <style>
        body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
        .container { max-width: 600px; margin: 0 auto; padding: 20px; }
        .header { background: #667eea; color: white; padding: 20px; text-align: center; }
        .content { padding: 30px 20px; background: white; }
        .cta-button { 
            display: inline-block; 
            background: #667eea; 
            color: white; 
            padding: 12px 24px; 
            text-decoration: none; 
            border-radius: 5px; 
            margin: 20px 0;
        }
        .footer { padding: 20px; text-align: center; color: #666; font-size: 12px; }
    </style>
</head>
<body>
    <div class="container">
        <div class="header">
            <h1>${personalizations.agencyName}</h1>
        </div>
        <div class="content">
            <p style="white-space: pre-line;">${content}</p>
            <p><a href="#" class="cta-button">${callToAction}</a></p>
        </div>
        <div class="footer">
            <p>Best regards,<br>The ${personalizations.agencyName} Team</p>
            <p>This email was sent to ${routingData.clientInfo.primaryContact?.email}</p>
        </div>
    </div>
</body>
</html>
`;

return [{
  json: {
    ...routingData,
    communicationContent: {
      subject,
      content,
      htmlContent,
      callToAction,
      attachments,
      templateUsed: template?.name || 'default',
      personalizations
    }
  }
}];

// Send SMS notification for urgent communications
const communicationData = $input.first().json;
const clientInfo = communicationData.clientInfo;
const content = communicationData.communicationContent;

// Create SMS-friendly message (160 character limit)
const smsMessage = `URGENT: ${clientInfo.companyName} - ${content.subject.substring(0, 100)}. Check email for details. - ${communicationData.personalizations.agencyName}`;

// SMS sending would integrate with services like Twilio, AWS SNS, etc.
// For now, we'll log the SMS and create a notification record

console.log('SMS sent to:', clientInfo.primaryContact.phone);
console.log('SMS message:', smsMessage);

return [{
  json: {
    ...communicationData,
    smsNotification: {
      phoneNumber: clientInfo.primaryContact.phone,
      message: smsMessage,
      sentAt: new Date().toISOString(),
      status: 'sent',
      provider: 'twilio' // Would be actual provider
    }
  }
}];

