parameters.jsCode:
// Schedule follow-up communications
const communicationData = $input.first().json;
const workflowType = communicationData.workflowType;

let followUpDelayHours = 0;
let followUpType = '';

switch (workflowType) {
  case 'project_completion':
    followUpDelayHours = 72; // 3 days
    followUpType = 'feedback_collection';
    break;
  case 'issue_resolution':
    followUpDelayHours = 24; // 1 day
    followUpType = 'resolution_confirmation';
    break;
  case 'payment_reminder':
    followUpDelayHours = 168; // 7 days
    followUpType = 'payment_followup';
    break;
}

const followUpTime = new Date(Date.now() + followUpDelayHours * 60 * 60 * 1000);

// Create follow-up webhook payload
const followUpPayload = {
  eventType: followUpType,
  clientId: communicationData.clientInfo.id,
  originalCommunication: workflowType,
  scheduledFor: followUpTime.toISOString(),
  automation: true
};

// In production, this would schedule the follow-up via a job queue or delayed webhook
console.log('Follow-up scheduled:', followUpPayload);

return [{
  json: {
    ...communicationData,
    followUpScheduled: {
      type: followUpType,
      scheduledFor: followUpTime.toISOString(),
      delayHours: followUpDelayHours,
      payload: followUpPayload
    }
  }
}];

// Error handling and recovery
const error = $input.first().json;
const clientData = $('Get Client Data').item?.json?.data;

console.error('Client Communication Workflow Error:', {
  clientId: clientData?.id,
  clientName: clientData?.company_name,
  error: error,
  timestamp: new Date().toISOString()
});

// Attempt to notify team about failed communication
return [{
  json: {
    error: true,
    clientId: clientData?.id || 'unknown',
    clientName: clientData?.company_name || 'unknown',
    errorMessage: error.message || 'Unknown error occurred',
    timestamp: new Date().toISOString(),
    workflow: 'client-communication-automation',
    requiresManualIntervention: true
  }
}];
