parameters.jsonBody:
={
  "template": "client_welcome_package",
  "to": "{{ $('Create Onboarding Plan').item.json.client.email }}",
  "cc": ["{{ $('Create Onboarding Plan').item.json.team_assignments.account_manager }}@agency.com"],
  "data": {
    "client_name": "{{ $('Create Onboarding Plan').item.json.client.name }}",
    "company_name": "{{ $('Create Onboarding Plan').item.json.client.company }}",
    "account_manager": "{{ $('Create Onboarding Plan').item.json.team_assignments.account_manager }}",
    "project_manager": "{{ $('Create Onboarding Plan').item.json.team_assignments.project_manager }}",
    "onboarding_timeline": "{{ $('Create Onboarding Plan').item.json.timeline.estimated_completion_days }}",
    "portal_url": "https://portal.agency.com/{{ $('Create Onboarding Plan').item.json.client.subdomain }}",
    "next_steps": [
      "Review welcome package and company information",
      "Complete client onboarding questionnaire",
      "Schedule discovery call with strategy team",
      "Await portal access credentials (within 24 hours)"
    ],
    "contact_information": {
      "phone": "+1-555-0123",
      "email": "support@agency.com",
      "emergency_contact": "urgent@agency.com"
    },
    "attachments": [
      {
        "name": "Welcome_Package.pdf",
        "description": "Complete guide to our services and processes"
      },
      {
        "name": "Onboarding_Questionnaire.pdf",
        "description": "Please complete and return within 3 business days"
      }
    ]
  },
  "priority": "high",
  "track_opens": true,
  "track_clicks": true
}

={
  "client_id": "{{ $('Create Onboarding Plan').item.json.client.id }}",
  "portal_name": "{{ $('Create Onboarding Plan').item.json.client.company }} Portal",
  "subdomain": "{{ $('Create Onboarding Plan').item.json.client.company.toLowerCase().replace(/[^a-z0-9]/g, '-') }}",
  "owner_email": "{{ $('Create Onboarding Plan').item.json.client.email }}",
  "owner_name": "{{ $('Create Onboarding Plan').item.json.client.name }}",
  "branding": {
    "company_name": "{{ $('Create Onboarding Plan').item.json.client.company }}",
    "primary_color": "{{ $('Create Onboarding Plan').item.json.client.brand_colors?.primary || '#6366f1' }}",
    "logo_url": "{{ $('Create Onboarding Plan').item.json.client.logo_url }}"
  },
  "initial_widgets": [
    "overview_stats",
    "seo_rankings",
    "website_traffic",
    "recent_activity"
  ],
  "access_settings": {
    "auth_method": "email_link",
    "session_timeout": 480,
    "allow_downloads": true
  },
  "communication_settings": {
    "email_notifications": true,
    "notification_frequency": "weekly",
    "enable_chat": true
  }
}

={
  "payment_terms": {
    "net_days": {{ $('Create Onboarding Plan').item.json.client.payment_terms?.net_days || 30 }},
    "late_fee_percentage": {{ $('Create Onboarding Plan').item.json.client.payment_terms?.late_fee_percentage || 1.5 }},
    "auto_payment": {{ $('Create Onboarding Plan').item.json.client.auto_payment || false }}
  },
  "billing_preferences": {
    "currency": "{{ $('Create Onboarding Plan').item.json.client.currency || 'USD' }}",
    "billing_cycle": "{{ $('Create Onboarding Plan').item.json.client.billing_cycle || 'monthly' }}",
    "invoice_delivery": "email",
    "auto_send_invoices": true
  },
  "services": [
    {
      "service_type": "{{ $('Create Onboarding Plan').item.json.client.service_type || 'seo' }}",
      "billing_type": "{{ $('Create Onboarding Plan').item.json.client.billing_type || 'monthly' }}",
      "amount": {{ $('Create Onboarding Plan').item.json.client.monthly_retainer || 2500 }},
      "start_date": "{{ $('Create Onboarding Plan').item.json.timeline.start_date }}",
      "auto_renew": true
    }
  ],
  "setup_fee": {{ $('Create Onboarding Plan').item.json.client.setup_fee || 500 }},
  "create_setup_invoice": true,
  "payment_methods": {
    "stripe_customer_id": "{{ $('Create Onboarding Plan').item.json.client.stripe_customer_id }}",
    "preferred_method": "{{ $('Create Onboarding Plan').item.json.client.preferred_payment_method || 'credit_card' }}"
  }
}

={
  "meeting_type": "discovery_call",
  "title": "{{ $('Create Onboarding Plan').item.json.client.company }} - Discovery & Strategy Session",
  "duration_minutes": 90,
  "attendees": [
    {
      "email": "{{ $('Create Onboarding Plan').item.json.client.email }}",
      "name": "{{ $('Create Onboarding Plan').item.json.client.name }}",
      "role": "client"
    },
    {
      "email": "{{ $('Create Onboarding Plan').item.json.team_assignments.account_manager }}@agency.com",
      "role": "account_manager"
    },
    {
      "email": "{{ $('Create Onboarding Plan').item.json.team_assignments.strategy_lead }}@agency.com",
      "role": "strategy_lead"
    }
  ],
  "preferred_times": [
    {
      "date": "{{ new Date(Date.now() + 3 * 24 * 60 * 60 * 1000).toISOString().split('T')[0] }}",
      "time_slots": ["10:00", "14:00", "15:30"]
    },
    {
      "date": "{{ new Date(Date.now() + 4 * 24 * 60 * 60 * 1000).toISOString().split('T')[0] }}",
      "time_slots": ["09:00", "11:00", "13:00"]
    }
  ],
  "meeting_agenda": [
    "Welcome and introductions",
    "Review current digital presence",
    "Discuss business goals and objectives",
    "Identify opportunities and challenges",
    "Present initial strategy recommendations",
    "Define success metrics and KPIs",
    "Outline project timeline and next steps"
  ],
  "preparation_materials": [
    "Current website analytics (if available)",
    "List of current marketing activities",
    "Competitor analysis (internal)",
    "Business objectives and goals documentation"
  ],
  "auto_send_calendar_invite": true,
  "send_reminder_24h": true,
  "send_reminder_2h": true,
  "include_zoom_link": true
}

={
  "follow_up_type": "onboarding_progress",
  "client_id": "{{ $('Create Onboarding Plan').item.json.client.id }}",
  "schedule": [
    {
      "delay_hours": 24,
      "action": "check_email_opened",
      "description": "Check if welcome email was opened, send reminder if not"
    },
    {
      "delay_hours": 72,
      "action": "questionnaire_reminder",
      "description": "Send reminder to complete onboarding questionnaire"
    },
    {
      "delay_hours": 168,
      "action": "discovery_call_reminder",
      "description": "Reminder about upcoming discovery call"
    },
    {
      "delay_hours": 336,
      "action": "onboarding_completion_check",
      "description": "Check onboarding progress and escalate if needed"
    }
  ],
  "escalation_rules": {
    "no_response_after_hours": 168,
    "escalate_to": "{{ $('Create Onboarding Plan').item.json.team_assignments.account_manager }}@agency.com",
    "cc_management": true
  },
  "success_triggers": [
    "questionnaire_completed",
    "discovery_call_completed",
    "first_invoice_paid",
    "portal_first_login"
  ]
}

={
  "event_type": "client_onboarding_automation",
  "client_data": {
    "client_id": "{{ $('Create Onboarding Plan').item.json.client.id }}",
    "company_name": "{{ $('Create Onboarding Plan').item.json.client.company }}",
    "service_type": "{{ $('Create Onboarding Plan').item.json.client.service_type }}",
    "monthly_value": {{ $('Create Onboarding Plan').item.json.client.monthly_retainer || 0 }},
    "acquisition_source": "{{ $('Create Onboarding Plan').item.json.client.acquisition_source }}"
  },
  "onboarding_metrics": {
    "total_steps": {{ $('Create Onboarding Plan').item.json.onboarding_steps.length }},
    "automated_steps": {{ $('Create Onboarding Plan').item.json.onboarding_steps.filter(step => step.auto_executable).length }},
    "estimated_completion_days": {{ $('Create Onboarding Plan').item.json.timeline.estimated_completion_days }},
    "team_members_assigned": 3
  },
  "automation_success": {
    "welcome_email_sent": true,
    "portal_created": true,
    "project_structure_setup": true,
    "billing_configured": true,
    "communication_channels_setup": true
  },
  "timestamp": "{{ new Date().toISOString() }}"
}
