// Normalize Mailchimp webhook data
const webhookData = $input.all()[0].json;

// Map Mailchimp event types to our system events
const eventMapping = {
  'opened': 'email_opened',
  'clicked': 'email_clicked',
  'unsubscribed': 'email_unsubscribed',
  'bounced': 'email_bounced',
  'sent': 'email_sent',
  'delivered': 'email_delivered'
};

const normalizedData = {
  platform: 'mailchimp',
  event_type: eventMapping[webhookData.type] || webhookData.type,
  email: webhookData.data?.email || webhookData.email,
  campaign_id: webhookData.data?.campaign_id,
  campaign_name: webhookData.data?.campaign_title,
  timestamp: webhookData.fired_at || new Date().toISOString(),
  engagement_data: {
    opens: webhookData.data?.opens || 0,
    clicks: webhookData.data?.clicks || 0,
    bounces: webhookData.data?.bounces || 0,
    unsubs: webhookData.data?.unsubs || 0,
    url_clicked: webhookData.data?.url,
    ip_address: webhookData.data?.ip,
    user_agent: webhookData.data?.user_agent
  },
  list_info: {
    list_id: webhookData.data?.list_id,
    segment_id: webhookData.data?.segment_id
  },
  raw_data: webhookData
};

// Calculate engagement score
let engagement_score = 0;
if (normalizedData.event_type === 'email_opened') engagement_score = 10;
if (normalizedData.event_type === 'email_clicked') engagement_score = 25;
if (normalizedData.event_type === 'email_unsubscribed') engagement_score = -50;
if (normalizedData.event_type === 'email_bounced') engagement_score = -25;

normalizedData.engagement_score = engagement_score;

return { json: normalizedData };

// Normalize SendGrid webhook data
const webhookData = $input.all()[0].json;

// Handle SendGrid batch events
const events = Array.isArray(webhookData) ? webhookData : [webhookData];
const processedEvents = [];

events.forEach(event => {
  const eventMapping = {
    'open': 'email_opened',
    'click': 'email_clicked',
    'unsubscribe': 'email_unsubscribed',
    'bounce': 'email_bounced',
    'delivered': 'email_delivered',
    'processed': 'email_sent',
    'dropped': 'email_dropped',
    'deferred': 'email_deferred',
    'spamreport': 'email_spam'
  };

  const normalizedEvent = {
    platform: 'sendgrid',
    event_type: eventMapping[event.event] || event.event,
    email: event.email,
    campaign_id: event.marketing_campaign_id || event.asm_group_id,
    campaign_name: event.marketing_campaign_name,
    timestamp: new Date(event.timestamp * 1000).toISOString(),
    engagement_data: {
      opens: event.event === 'open' ? 1 : 0,
      clicks: event.event === 'click' ? 1 : 0,
      bounces: event.event === 'bounce' ? 1 : 0,
      url_clicked: event.url,
      ip_address: event.ip,
      user_agent: event.useragent,
      reason: event.reason,
      bounce_type: event.type
    },
    categories: event.category || [],
    raw_data: event
  };

  // Calculate engagement score
  let engagement_score = 0;
  if (event.event === 'open') engagement_score = 10;
  if (event.event === 'click') engagement_score = 25;
  if (event.event === 'unsubscribe') engagement_score = -50;
  if (event.event === 'bounce') engagement_score = -25;
  if (event.event === 'spamreport') engagement_score = -75;

  normalizedEvent.engagement_score = engagement_score;
  processedEvents.push(normalizedEvent);
});

return processedEvents.map(event => ({ json: event }));

// Normalize Constant Contact webhook data
const webhookData = $input.all()[0].json;

const eventMapping = {
  'email_open': 'email_opened',
  'email_click': 'email_clicked',
  'email_bounce': 'email_bounced',
  'email_send': 'email_sent',
  'email_optout': 'email_unsubscribed'
};

const normalizedData = {
  platform: 'constant_contact',
  event_type: eventMapping[webhookData.event_type] || webhookData.event_type,
  email: webhookData.contact?.email_address,
  campaign_id: webhookData.campaign_activity_id,
  campaign_name: webhookData.campaign_activity?.subject,
  timestamp: webhookData.event_datetime || new Date().toISOString(),
  engagement_data: {
    opens: webhookData.event_type === 'email_open' ? 1 : 0,
    clicks: webhookData.event_type === 'email_click' ? 1 : 0,
    bounces: webhookData.event_type === 'email_bounce' ? 1 : 0,
    url_clicked: webhookData.click_url,
    bounce_code: webhookData.bounce_code,
    bounce_description: webhookData.bounce_description
  },
  contact_data: {
    contact_id: webhookData.contact?.contact_id,
    first_name: webhookData.contact?.first_name,
    last_name: webhookData.contact?.last_name
  },
  raw_data: webhookData
};

// Calculate engagement score
let engagement_score = 0;
if (normalizedData.event_type === 'email_opened') engagement_score = 10;
if (normalizedData.event_type === 'email_clicked') engagement_score = 25;
if (normalizedData.event_type === 'email_unsubscribed') engagement_score = -50;
if (normalizedData.event_type === 'email_bounced') engagement_score = -25;

normalizedData.engagement_score = engagement_score;

return { json: normalizedData };

// Calculate new behavioral score based on email engagement
const emailData = $node["Route by Platform"].json;
const contactData = $node["Find Contact in CRM"].json.contact;

// Current behavioral score
const currentScore = contactData.behavioral_score || 0;

// Email engagement scoring matrix
const scoringMatrix = {
  'email_opened': {
    base_score: 10,
    frequency_multiplier: 0.8, // Diminishing returns for repeated opens
    time_decay: 0.9 // Recent activity scores higher
  },
  'email_clicked': {
    base_score: 25,
    frequency_multiplier: 1.2, // Clicks maintain value
    time_decay: 0.95
  },
  'email_unsubscribed': {
    base_score: -50,
    frequency_multiplier: 1.0,
    time_decay: 1.0
  },
  'email_bounced': {
    base_score: -25,
    frequency_multiplier: 1.0,
    time_decay: 1.0
  },
  'email_spam': {
    base_score: -75,
    frequency_multiplier: 1.0,
    time_decay: 1.0
  }
};

// Get recent email activity (last 30 days)
const thirtyDaysAgo = new Date();
thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

const recentEmailActivity = contactData.activities?.filter(activity => 
  activity.type.startsWith('email_') && 
  new Date(activity.created_at) > thirtyDaysAgo
) || [];

// Count event types
const eventCounts = {};
recentEmailActivity.forEach(activity => {
  eventCounts[activity.type] = (eventCounts[activity.type] || 0) + 1;
});

// Calculate score adjustment
const scoring = scoringMatrix[emailData.event_type] || { base_score: 0, frequency_multiplier: 1.0, time_decay: 1.0 };
const frequency_count = eventCounts[emailData.event_type] || 0;
const frequency_factor = Math.pow(scoring.frequency_multiplier, frequency_count);
const time_factor = scoring.time_decay;

const score_adjustment = Math.round(scoring.base_score * frequency_factor * time_factor);
const new_score = Math.max(0, Math.min(100, currentScore + score_adjustment));

// Determine lead temperature
let lead_temperature = 'cold';
if (new_score >= 70) lead_temperature = 'hot';
else if (new_score >= 40) lead_temperature = 'warm';

// Check if score crossed important thresholds
const thresholds_crossed = [];
if (currentScore < 40 && new_score >= 40) thresholds_crossed.push('qualified_lead');
if (currentScore < 70 && new_score >= 70) thresholds_crossed.push('sales_ready');
if (currentScore >= 40 && new_score < 40) thresholds_crossed.push('cooling_down');

const result = {
  contact_id: contactData._id,
  email: emailData.email,
  previous_score: currentScore,
  score_adjustment: score_adjustment,
  new_score: new_score,
  lead_temperature: lead_temperature,
  thresholds_crossed: thresholds_crossed,
  engagement_pattern: {
    event_type: emailData.event_type,
    platform: emailData.platform,
    campaign_id: emailData.campaign_id,
    frequency_count: frequency_count,
    recent_activity_count: recentEmailActivity.length
  },
  timestamp: new Date().toISOString()
};

return { json: result };

