// Batch sync configuration for different platforms
const platforms = [
  {
    name: 'mailchimp',
    api_endpoint: 'https://us1.api.mailchimp.com/3.0',
    batch_size: 100,
    lookback_hours: 4
  },
  {
    name: 'sendgrid',
    api_endpoint: 'https://api.sendgrid.com/v3',
    batch_size: 500,
    lookback_hours: 4
  },
  {
    name: 'constant_contact',
    api_endpoint: 'https://api.cc.email/v3',
    batch_size: 50,
    lookback_hours: 4
  }
];

const timestamp = new Date().toISOString();
const lookbackTime = new Date(Date.now() - (4 * 60 * 60 * 1000)).toISOString();

return platforms.map(platform => ({
  json: {
    platform: platform.name,
    api_endpoint: platform.api_endpoint,
    batch_size: platform.batch_size,
    since: lookbackTime,
    timestamp: timestamp
  }
}));

// Process batch data and convert to individual events
const batchData = $json;
const platform = $node["Batch Platform Router"].json.platform;

if (!batchData.reports && !batchData.stats) {
  return [];
}

const events = [];

if (platform === 'mailchimp' && batchData.reports) {
  batchData.reports.forEach(report => {
    // Create events for each report metric
    events.push({
      platform: 'mailchimp',
      event_type: 'campaign_summary',
      campaign_id: report.campaign_id,
      campaign_name: report.campaign_title,
      timestamp: report.send_time,
      batch_metrics: {
        emails_sent: report.emails_sent,
        opens: report.opens.opens_total,
        unique_opens: report.opens.unique_opens,
        clicks: report.clicks.clicks_total,
        unique_clicks: report.clicks.unique_clicks,
        unsubscribes: report.unsubscribed.unsubscribes
      }
    });
  });
}

if (platform === 'sendgrid' && batchData.stats) {
  batchData.stats.forEach(stat => {
    events.push({
      platform: 'sendgrid',
      event_type: 'campaign_summary',
      timestamp: stat.date,
      batch_metrics: {
        emails_sent: stat.delivered,
        opens: stat.opens,
        unique_opens: stat.unique_opens,
        clicks: stat.clicks,
        unique_clicks: stat.unique_clicks,
        bounces: stat.bounces,
        spam_reports: stat.spam_reports
      }
    });
  });
}

return events.map(event => ({ json: event }));
