// Determine report type and date range
const triggerNode = $workflow.lastNode.name;

let reportConfig = {
  report_type: '',
  period: {
    start_date: '',
    end_date: ''
  },
  frequency: '',
  recipients: [],
  include_sections: []
};

const now = new Date();
const currentDate = new Date(now.getFullYear(), now.getMonth(), now.getDate());

if (triggerNode === 'Weekly Report Trigger') {
  // Weekly report - Monday to Sunday of previous week
  const lastSunday = new Date(currentDate);
  lastSunday.setDate(currentDate.getDate() - currentDate.getDay() - 7);
  const lastMonday = new Date(lastSunday);
  lastMonday.setDate(lastSunday.getDate() - 6);

  reportConfig = {
    report_type: 'weekly_financial_summary',
    period: {
      start_date: lastMonday.toISOString(),
      end_date: lastSunday.toISOString()
    },
    frequency: 'weekly',
    recipients: [
      'management@agency.com',
      'finance@agency.com',
      'ceo@agency.com'
    ],
    include_sections: [
      'revenue_summary',
      'expense_summary',
      'cash_flow',
      'client_payments',
      'outstanding_invoices',
      'project_profitability',
      'team_utilization',
      'key_metrics'
    ]
  };
} else if (triggerNode === 'Monthly Report Trigger') {
  // Monthly report - Previous full month
  const lastMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1);
  const thisMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
  const lastDayOfMonth = new Date(thisMonth.getTime() - 1);

  reportConfig = {
    report_type: 'monthly_financial_report',
    period: {
      start_date: lastMonth.toISOString(),
      end_date: lastDayOfMonth.toISOString()
    },
    frequency: 'monthly',
    recipients: [
      'management@agency.com',
      'finance@agency.com',
      'ceo@agency.com',
      'board@agency.com'
    ],
    include_sections: [
      'executive_summary',
      'revenue_analysis',
      'expense_breakdown',
      'profit_loss_statement',
      'cash_flow_statement',
      'balance_sheet_summary',
      'client_revenue_breakdown',
      'project_profitability_analysis',
      'budget_vs_actual',
      'forecasting',
      'kpi_dashboard',
      'recommendations'
    ]
  };
}

return [{ json: reportConfig }];

// Compile and analyze all financial data
const reportConfig = $('Determine Report Configuration').first().json;
const financialData = $('Fetch Financial Data').first().json;
const projectData = $('Fetch Project Analytics').first().json;
const teamData = $('Fetch Team Utilization').first().json;

// Calculate key metrics and insights
const compiledReport = {
  report_metadata: {
    report_type: reportConfig.report_type,
    period: reportConfig.period,
    generated_at: new Date().toISOString(),
    frequency: reportConfig.frequency,
    data_sources: ['financial_system', 'project_management', 'time_tracking']
  },

  executive_summary: {
    total_revenue: financialData.revenue?.total || 0,
    total_expenses: financialData.expenses?.total || 0,
    net_profit: (financialData.revenue?.total || 0) - (financialData.expenses?.total || 0),
    profit_margin: financialData.revenue?.total > 0 ? 
      (((financialData.revenue?.total || 0) - (financialData.expenses?.total || 0)) / (financialData.revenue?.total || 0)) * 100 : 0,
    cash_flow: financialData.cash_flow?.net_change || 0,
    outstanding_amount: financialData.outstanding?.total_amount || 0,
    active_projects: projectData.summary?.total_projects || 0,
    team_utilization_avg: teamData.overall?.average_utilization || 0
  },

  revenue_analysis: {
    total_revenue: financialData.revenue?.total || 0,
    recurring_revenue: financialData.revenue?.recurring || 0,
    one_time_revenue: financialData.revenue?.one_time || 0,
    growth_rate: financialData.revenue?.growth_rate || 0,
    revenue_by_service: financialData.revenue?.by_service || [],
    revenue_by_client: financialData.revenue?.by_client || [],
    top_performing_clients: (financialData.revenue?.by_client || []).slice(0, 5)
  },

  expense_breakdown: {
    total_expenses: financialData.expenses?.total || 0,
    by_category: financialData.expenses?.by_category || [],
    fixed_expenses: financialData.expenses?.fixed || 0,
    variable_expenses: financialData.expenses?.variable || 0,
    team_costs: financialData.expenses?.team_costs || 0,
    overhead: financialData.expenses?.overhead || 0,
    growth_rate: financialData.expenses?.growth_rate || 0
  },

  project_profitability: {
    total_projects: projectData.summary?.total_projects || 0,
    profitable_projects: projectData.summary?.profitable_projects || 0,
    average_margin: projectData.summary?.average_profit_margin || 0,
    total_project_revenue: projectData.summary?.total_revenue || 0,
    total_project_costs: projectData.summary?.total_costs || 0,
    projects_breakdown: projectData.projects?.slice(0, 10) || [],
    underperforming_projects: (projectData.projects || []).filter(p => p.profit_margin < 20)
  },

  team_performance: {
    total_team_members: teamData.team_metrics?.length || 0,
    average_utilization: teamData.overall?.average_utilization || 0,
    total_billable_hours: teamData.overall?.total_billable_hours || 0,
    total_logged_hours: teamData.overall?.total_logged_hours || 0,
    efficiency_score: teamData.overall?.efficiency_score || 0,
    top_performers: (teamData.team_metrics || []).slice(0, 5),
    underutilized_resources: (teamData.team_metrics || []).filter(m => m.utilization_rate < 60)
  },

  cash_flow: {
    opening_balance: financialData.cash_flow?.opening_balance || 0,
    closing_balance: financialData.cash_flow?.closing_balance || 0,
    net_change: financialData.cash_flow?.net_change || 0,
    cash_inflows: financialData.cash_flow?.inflows || 0,
    cash_outflows: financialData.cash_flow?.outflows || 0,
    accounts_receivable: financialData.outstanding?.total_amount || 0,
    days_sales_outstanding: financialData.metrics?.days_sales_outstanding || 0
  },

  key_insights: [],
  recommendations: [],
  alerts: []
};

// Generate insights based on data analysis
const insights = [];
const recommendations = [];
const alerts = [];

// Profit margin analysis
if (compiledReport.executive_summary.profit_margin < 20) {
  insights.push({
    type: 'warning',
    title: 'Low Profit Margin',
    description: `Current profit margin of ${compiledReport.executive_summary.profit_margin.toFixed(1)}% is below the target of 20%`,
    impact: 'high'
  });
  recommendations.push({
    priority: 'high',
    category: 'profitability',
    action: 'Review pricing strategy and operational efficiency',
    expected_impact: 'Increase profit margin by 5-10%'
  });
}

// Cash flow analysis
if (compiledReport.cash_flow.net_change < 0) {
  alerts.push({
    type: 'critical',
    title: 'Negative Cash Flow',
    description: `Cash flow decreased by $${Math.abs(compiledReport.cash_flow.net_change)}`,
    action_required: true
  });
}

// Outstanding invoices analysis
if (compiledReport.executive_summary.outstanding_amount > compiledReport.executive_summary.total_revenue * 0.3) {
  insights.push({
    type: 'warning',
    title: 'High Outstanding Amount',
    description: `Outstanding invoices represent ${((compiledReport.executive_summary.outstanding_amount / compiledReport.executive_summary.total_revenue) * 100).toFixed(1)}% of total revenue`,
    impact: 'medium'
  });
  recommendations.push({
    priority: 'medium',
    category: 'collections',
    action: 'Implement more aggressive collection procedures',
    expected_impact: 'Reduce outstanding invoices by 20%'
  });
}

// Team utilization analysis
if (compiledReport.team_performance.average_utilization < 70) {
  insights.push({
    type: 'info',
    title: 'Low Team Utilization',
    description: `Average team utilization of ${compiledReport.team_performance.average_utilization}% indicates capacity for growth`,
    impact: 'medium'
  });
  recommendations.push({
    priority: 'medium',
    category: 'resource_management',
    action: 'Focus on business development to increase team utilization',
    expected_impact: 'Increase revenue capacity by 20-30%'
  });
}

// Project profitability analysis
if (compiledReport.project_profitability.underperforming_projects.length > 0) {
  alerts.push({
    type: 'warning',
    title: 'Underperforming Projects',
    description: `${compiledReport.project_profitability.underperforming_projects.length} projects have profit margins below 20%`,
    action_required: true
  });
}

compiledReport.key_insights = insights;
compiledReport.recommendations = recommendations;
compiledReport.alerts = alerts;

return [{ json: compiledReport }];

