// Advanced Lead Scoring Algorithm
const activity = items[0].json;
const enrichmentData = {
  hunter: items[1]?.json || {},
  clearbit_person: items[2]?.json || {},
  clearbit_company: items[3]?.json || {},
  linkedin: items[4]?.json || {}
};

// Calculate enhanced lead score
let baseScore = activity.leadScore || 0;
let scoringFactors = [];

// Email verification score
if (enrichmentData.hunter.result === 'deliverable') {
  baseScore += 10;
  scoringFactors.push({
    factor: 'Email Verified',
    impact: 10,
    reason: 'Email deliverability confirmed'
  });
}

// Job title scoring
if (enrichmentData.clearbit_person.employment?.title) {
  const title = enrichmentData.clearbit_person.employment.title.toLowerCase();
  const executiveTitles = ['ceo', 'cto', 'cfo', 'vp', 'director', 'head', 'chief'];
  const managerTitles = ['manager', 'lead', 'senior'];

  if (executiveTitles.some(t => title.includes(t))) {
    baseScore += 20;
    scoringFactors.push({
      factor: 'Executive Role',
      impact: 20,
      reason: 'Decision maker identified'
    });
  } else if (managerTitles.some(t => title.includes(t))) {
    baseScore += 10;
    scoringFactors.push({
      factor: 'Management Role',
      impact: 10,
      reason: 'Management level position'
    });
  }
}

// Company size scoring
if (enrichmentData.clearbit_company.metrics?.employees) {
  const employeeCount = enrichmentData.clearbit_company.metrics.employees;
  if (employeeCount > 1000) {
    baseScore += 15;
    scoringFactors.push({
      factor: 'Enterprise Company',
      impact: 15,
      reason: `Large company (${employeeCount} employees)`
    });
  } else if (employeeCount > 100) {
    baseScore += 10;
    scoringFactors.push({
      factor: 'Mid-size Company',
      impact: 10,
      reason: `Mid-size company (${employeeCount} employees)`
    });
  } else if (employeeCount > 10) {
    baseScore += 5;
    scoringFactors.push({
      factor: 'Small Company',
      impact: 5,
      reason: `Small company (${employeeCount} employees)`
    });
  }
}

// Industry scoring
if (enrichmentData.clearbit_company.category?.industry) {
  const highValueIndustries = ['technology', 'software', 'saas', 'finance', 'healthcare'];
  const industry = enrichmentData.clearbit_company.category.industry.toLowerCase();

  if (highValueIndustries.some(i => industry.includes(i))) {
    baseScore += 8;
    scoringFactors.push({
      factor: 'High-Value Industry',
      impact: 8,
      reason: `Target industry: ${enrichmentData.clearbit_company.category.industry}`
    });
  }
}

// LinkedIn presence scoring
if (enrichmentData.linkedin.profile_url) {
  baseScore += 5;
  scoringFactors.push({
    factor: 'LinkedIn Presence',
    impact: 5,
    reason: 'Professional LinkedIn profile found'
  });
}

// Technology usage (if available)
if (enrichmentData.clearbit_company.tech) {
  const competitorTech = ['hubspot', 'salesforce', 'marketo'];
  const complementaryTech = ['slack', 'zoom', 'microsoft'];

  const usedTech = enrichmentData.clearbit_company.tech.map(t => t.toLowerCase());

  if (competitorTech.some(tech => usedTech.includes(tech))) {
    baseScore += 12;
    scoringFactors.push({
      factor: 'Competitor Tool Usage',
      impact: 12,
      reason: 'Uses competing solutions - potential switcher'
    });
  }

  if (complementaryTech.some(tech => usedTech.includes(tech))) {
    baseScore += 6;
    scoringFactors.push({
      factor: 'Complementary Tech Stack',
      impact: 6,
      reason: 'Uses complementary tools - good fit'
    });
  }
}

// Ensure score stays within bounds
baseScore = Math.min(100, Math.max(0, baseScore));

// Determine lead quality grade
let leadGrade;
if (baseScore >= 90) leadGrade = 'A+';
else if (baseScore >= 80) leadGrade = 'A';
else if (baseScore >= 70) leadGrade = 'B';
else if (baseScore >= 60) leadGrade = 'C';
else if (baseScore >= 50) leadGrade = 'D';
else leadGrade = 'F';

// Calculate conversion probability
const conversionProbability = Math.min(95, (baseScore * 0.8) + (scoringFactors.length * 2));

// Estimate deal value based on company size and industry
let estimatedValue = 5000; // Base value
if (enrichmentData.clearbit_company.metrics?.employees > 1000) estimatedValue *= 4;
else if (enrichmentData.clearbit_company.metrics?.employees > 100) estimatedValue *= 2.5;
else if (enrichmentData.clearbit_company.metrics?.employees > 10) estimatedValue *= 1.5;

const result = {
  ...activity,
  enrichedData: enrichmentData,
  enhancedScore: {
    score: baseScore,
    grade: leadGrade,
    conversionProbability: Math.round(conversionProbability),
    estimatedValue: Math.round(estimatedValue),
    scoringFactors: scoringFactors,
    lastUpdated: new Date().toISOString()
  },
  enrichmentSources: {
    hunter: enrichmentData.hunter.result ? 'success' : 'failed',
    clearbit_person: enrichmentData.clearbit_person.id ? 'success' : 'failed',
    clearbit_company: enrichmentData.clearbit_company.id ? 'success' : 'failed',
    linkedin: enrichmentData.linkedin.profile_url ? 'success' : 'failed'
  },
  triggers: []
};

// Add triggered actions based on score
if (baseScore >= 90) {
  result.triggers.push({
    name: 'Hot Lead Alert',
    action: 'immediate_sales_notification',
    priority: 'high'
  });
}

if (baseScore >= 80) {
  result.triggers.push({
    name: 'High Score Follow-up',
    action: 'personalized_email_sequence',
    priority: 'medium'
  });
}

if (activity.type === 'pricing_view' && baseScore >= 60) {
  result.triggers.push({
    name: 'Pricing Interest',
    action: 'send_pricing_info',
    priority: 'medium'
  });
}

return [result];

