parameters.functionCode:
// Anti-spam and fraud detection
const activity = items[0].json;

// Check for bot-like behavior
const botIndicators = [
  activity.metadata?.userAgent?.toLowerCase().includes('bot'),
  activity.metadata?.userAgent?.toLowerCase().includes('crawler'),
  activity.metadata?.userAgent?.toLowerCase().includes('spider'),
  !activity.metadata?.userAgent, // Missing user agent
  activity.metadata?.duration < 5 && activity.type === 'page_view', // Too short page view
  activity.timestamp && (Date.now() - new Date(activity.timestamp).getTime()) > 24 * 60 * 60 * 1000 // Activity too old
];

const botScore = botIndicators.filter(Boolean).length;

if (botScore >= 2) {
  return [{
    ...activity,
    fraudDetection: {
      isBot: true,
      score: botScore,
      reason: 'Multiple bot indicators detected',
      action: 'discard'
    }
  }];
}

// Check for rapid-fire activities (potential bot)
if (activity.metadata?.rapidFire && activity.metadata.rapidFire > 10) {
  return [{
    ...activity,
    fraudDetection: {
      isRapidFire: true,
      reason: 'Too many activities in short time',
      action: 'throttle'
    }
  }];
}

// Valid activity
return [{
  ...activity,
  fraudDetection: {
    isValid: true,
    score: 0,
    action: 'process'
  }
}];

