parameters.jsCode:
// Enhanced Social Media Lead Capture Engine
const inputData = $input.first().json;
const body = inputData.body || inputData;

// Determine platform from input or URL path
let platform = body.platform || 'unknown';
if (!platform || platform === 'unknown') {
  const path = inputData.path || '';
  if (path.includes('linkedin')) platform = 'linkedin';
  else if (path.includes('instagram')) platform = 'instagram';
  else if (path.includes('facebook')) platform = 'facebook';
  else if (path.includes('threads')) platform = 'threads';
  else if (path.includes('youtube')) platform = 'youtube';
  else if (path.includes('pinterest')) platform = 'pinterest';
}

// Validate required data
if (!body.interaction_type && !body.event_type) {
  return [{
    json: {
      error: true,
      message: 'Missing interaction_type or event_type',
      platform: platform
    }
  }];
}

// Platform configuration
const platformConfig = {
  linkedin: {
    name: 'LinkedIn',
    priority: 'very_high',
    lead_value_multiplier: 1.5
  },
  instagram: {
    name: 'Instagram',
    priority: 'high',
    lead_value_multiplier: 1.2
  },
  facebook: {
    name: 'Facebook',
    priority: 'high',
    lead_value_multiplier: 1.1
  },
  threads: {
    name: 'Threads',
    priority: 'very_high',
    lead_value_multiplier: 1.4
  },
  youtube: {
    name: 'YouTube',
    priority: 'medium',
    lead_value_multiplier: 1.0
  },
  pinterest: {
    name: 'Pinterest',
    priority: 'medium',
    lead_value_multiplier: 0.9
  }
};

// Extract contact information
const contactInfo = {
  full_name: body.full_name || body.name || body.sender?.name || '',
  username: body.username || body.sender?.username || '',
  email: body.email || '',
  company: body.company || body.sender?.company || '',
  title: body.title || body.headline || body.sender?.headline || '',
  location: body.location || body.sender?.location || '',
  profile_url: body.profile_url || body.sender?.profileUrl || '',
  follower_count: body.follower_count || body.followers || 0,
  verified: body.verified || body.is_verified || false
};

// Extract content information
const contentInfo = {
  message: body.message || body.content || body.text || '',
  interaction_type: body.interaction_type || body.event_type,
  post_url: body.post_url || body.url || '',
  timestamp: body.timestamp || new Date().toISOString()
};

// Calculate engagement score
const scoreMatrix = {
  linkedin: {
    'connection_request': 30,
    'message': 40,
    'comment': 20,
    'like': 8,
    'share': 25,
    'profile_view': 15
  },
  instagram: {
    'follow': 15,
    'like': 5,
    'comment': 18,
    'share': 22,
    'story_reply': 25,
    'direct_message': 35
  },
  facebook: {
    'like': 5,
    'comment': 15,
    'share': 20,
    'message': 30,
    'friend_request': 25
  },
  threads: {
    'follow': 20,
    'like': 8,
    'reply': 25,
    'repost': 22,
    'quote': 28
  },
  youtube: {
    'subscribe': 25,
    'like': 8,
    'comment': 18,
    'share': 20
  },
  pinterest: {
    'follow': 12,
    'like': 6,
    'comment': 15,
    'save': 18
  }
};

const baseScore = scoreMatrix[platform]?.[contentInfo.interaction_type] || 10;
let finalScore = baseScore;

// Apply multipliers
if (contactInfo.verified) finalScore *= 1.3;
if (contactInfo.company) finalScore *= 1.2;
if (contactInfo.follower_count > 1000) finalScore *= 1.1;
if (contactInfo.follower_count > 10000) finalScore *= 1.2;
if (platformConfig[platform]?.lead_value_multiplier) {
  finalScore *= platformConfig[platform].lead_value_multiplier;
}

// Determine lead quality
let qualityLevel = 'low';
let priority = 'normal';
if (finalScore >= 50) {
  qualityLevel = 'high';
  priority = 'urgent';
} else if (finalScore >= 25) {
  qualityLevel = 'medium';
  priority = 'high';
}

// Generate lead qualification
const leadData = {
  lead_id: `lead_${platform}_${Date.now()}`,
  platform: platform,
  platform_name: platformConfig[platform]?.name || platform,
  contact_info: contactInfo,
  content_info: contentInfo,

  lead_qualification: {
    score: Math.round(finalScore),
    quality_level: qualityLevel,
    priority: priority,
    qualification_factors: [
      contactInfo.verified ? 'verified_account' : null,
      contactInfo.company ? 'has_company' : null,
      contactInfo.follower_count > 1000 ? 'influencer' : null,
      platform === 'linkedin' ? 'professional_network' : null
    ].filter(Boolean)
  },

  enrichment_data: {
    estimated_value: Math.round(finalScore * 50), // $50 per point
    follow_up_timeline: priority === 'urgent' ? '2 hours' : priority === 'high' ? '24 hours' : '72 hours',
    platform_priority: platformConfig[platform]?.priority || 'medium'
  },

  processing_metadata: {
    processed_at: new Date().toISOString(),
    workflow_version: '2.0-fixed',
    data_completeness: Object.values(contactInfo).filter(v => v && v !== '').length / Object.keys(contactInfo).length
  }
};

return [{ json: leadData }];

