const input = $input.first().json;
const requiredFields = ['contactId', 'email'];
const missingFields = requiredFields.filter(field => !input[field]);

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

const sanitizedData = {
  contactId: String(input.contactId).trim(),
  email: String(input.email).toLowerCase().trim(),
  firstName: input.firstName ? String(input.firstName).trim() : '',
  lastName: input.lastName ? String(input.lastName).trim() : '',
  company: input.company ? String(input.company).trim() : '',
  jobTitle: input.jobTitle ? String(input.jobTitle).trim() : '',
  leadScore: parseInt(input.leadScore) || 0,
  lifecycleStage: input.lifecycleStage || 'lead',
  optedIn: Boolean(input.optedIn),
  source: input.source || 'webhook',
  timestamp: new Date().toISOString(),
  workflowId: $workflow.id,
  executionId: $execution.id,
  metadata: input.metadata || {}
};

if (!sanitizedData.email.includes('@')) {
  throw new Error('Invalid email format');
}

return [{ json: sanitizedData }];

const contactData = $input.first().json;
const originalData = $('Input Data Validator').first().json;

if (!contactData.success || !contactData.data) {
  throw new Error(`Failed to fetch contact: ${contactData.message || 'Unknown error'}`);
}

const contact = contactData.data;
const mergedData = {
  ...originalData,
  contact: {
    id: contact.id,
    firstName: contact.first_name || originalData.firstName,
    lastName: contact.last_name || originalData.lastName,
    email: contact.email || originalData.email,
    company: contact.company || originalData.company,
    jobTitle: contact.job_title || originalData.jobTitle,
    industry: contact.industry,
    companySize: contact.company_size,
    leadScore: contact.lead_score || originalData.leadScore,
    lifecycleStage: contact.lifecycle_stage || originalData.lifecycleStage,
    createdAt: contact.created_at,
    lastActivity: contact.last_activity_date,
    preferences: contact.preferences || {},
    assignedSalesRep: contact.assigned_sales_rep || {},
    tags: contact.tags || [],
    customFields: contact.custom_fields || {}
  }
};

return [{ json: mergedData }];

const originalData = $('Merge Contact Data').first().json;
const enrichmentResponse = $input.first().json;

let enrichedData = { ...originalData };

if (enrichmentResponse.success && enrichmentResponse.data) {
  const enrichment = enrichmentResponse.data;

  enrichedData.enrichment = {
    companySize: enrichment.company_size || originalData.contact.companySize,
    industry: enrichment.industry || originalData.contact.industry,
    revenue: enrichment.annual_revenue,
    employees: enrichment.employee_count,
    technologies: enrichment.technologies || [],
    socialProfiles: enrichment.social_profiles || {},
    painPoints: enrichment.identified_pain_points || [],
    decisionMakers: enrichment.decision_makers || [],
    competitorAnalysis: enrichment.competitors || [],
    buyingSignals: enrichment.buying_signals || []
  };

  enrichedData.contact.industry = enrichment.industry || enrichedData.contact.industry;
  enrichedData.contact.companySize = enrichment.company_size || enrichedData.contact.companySize;
}

return [{ json: enrichedData }];

