const engagementResponse = $input.first().json;
const originalData = $('Prepare Email Data').first().json;

if (!engagementResponse.success) {
  throw new Error(`Failed to fetch engagement data: ${engagementResponse.message}`);
}

const activities = engagementResponse.data.activities || [];
const engagementScore = engagementResponse.data.engagement_score || 0;

const engagementData = {
  ...originalData,
  engagement: {
    activities: activities,
    score: engagementScore,
    hasActivity: activities.length > 0,
    isHighEngagement: engagementScore >= 30,
    emailOpens: activities.filter(a => a.type === 'email_open').length,
    emailClicks: activities.filter(a => a.type === 'email_click').length,
    websiteVisits: activities.filter(a => a.type === 'website_visit').length,
    downloads: activities.filter(a => a.type === 'content_download').length
  }
};

return [{ json: engagementData }];

