const successInputs = $input.all();
let finalResult = {
  success: true,
  timestamp: new Date().toISOString(),
  workflowId: $workflow.id,
  executionId: $execution.id,
  status: 'completed'
};

if (successInputs.length > 0) {
  const mainData = successInputs[0].json;

  if (mainData.contact) {
    finalResult.contact = {
      id: mainData.contact.id,
      firstName: mainData.contact.firstName,
      lastName: mainData.contact.lastName,
      email: mainData.contact.email,
      company: mainData.contact.company,
      leadScore: mainData.scoreUpdate?.totalScore || mainData.contact.leadScore
    };
  }

  if (mainData.sequence) {
    finalResult.sequence = {
      id: mainData.sequence.id,
      type: mainData.sequence.type,
      step: mainData.sequence.step,
      name: mainData.sequence.name
    };
  }

  if (mainData.engagement) {
    finalResult.engagement = {
      score: mainData.engagement.score,
      level: mainData.scoreUpdate?.engagementLevel || 'unknown',
      activities: mainData.engagement.activities?.length || 0
    };
  }

  finalResult.outcome = mainData.scoreUpdate?.totalScore >= 85 ? 'sales_ready' : 'nurture_continue';
  finalResult.nextAction = finalResult.outcome === 'sales_ready' ? 'handoff_to_sales' : 'continue_sequence';
}

return [{ json: finalResult }];

const errorInputs = $input.all();
const workflowData = {
  workflowId: $workflow.id,
  executionId: $execution.id,
  timestamp: new Date().toISOString()
};

let errorDetails = {
  ...workflowData,
  errors: [],
  severity: 'medium',
  contactId: 'unknown'
};

errorInputs.forEach((errorInput, index) => {
  const error = errorInput.json;
  if (error.error || error.message) {
    errorDetails.errors.push({
      nodeIndex: index,
      error: error.error || error.message,
      code: error.code || 'UNKNOWN_ERROR',
      timestamp: new Date().toISOString()
    });
  }

  if (error.contactId) {
    errorDetails.contactId = error.contactId;
  }

  if (error.code === 'CRITICAL_FAILURE' || (error.statusCode && error.statusCode >= 500)) {
    errorDetails.severity = 'critical';
  }
});

const requiresEscalation = errorDetails.severity === 'critical' || errorDetails.errors.length > 2;

const errorReport = {
  ...errorDetails,
  requiresEscalation,
  escalationLevel: errorDetails.severity === 'critical' ? 3 : 2
};

console.error('Nurture Sequence Error Report:', errorReport);
return [{ json: errorReport }];

const errorData = $input.first().json;
const errorResponse = {
  success: false,
  timestamp: new Date().toISOString(),
  workflowId: errorData.workflowId,
  executionId: errorData.executionId,
  status: 'failed',
  error: {
    severity: errorData.severity,
    contactId: errorData.contactId,
    errorCount: errorData.errors.length,
    requiresEscalation: errorData.requiresEscalation,
    escalationLevel: errorData.escalationLevel,
    errors: errorData.errors
  },
  nextAction: 'retry_or_manual_intervention'
};

return [{ json: errorResponse }];
