// Input validation and data preprocessing
const input = $input.first().json;

// Validate required fields
const requiredFields = ['name', 'email', 'company'];
const missingFields = requiredFields.filter(field => !input[field]);

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

// Sanitize and validate email
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(input.email)) {
  throw new Error('Invalid email format');
}

// Generate unique client ID
const clientId = `client_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

// Clean and normalize data
const processedData = {
  id: clientId,
  name: input.name.trim(),
  email: input.email.toLowerCase().trim(),
  company: input.company.trim(),
  phone: input.phone?.trim() || '',
  website: input.website?.trim() || '',
  service_type: input.service_type || 'seo',
  monthly_retainer: parseFloat(input.monthly_retainer) || 2500,
  billing_cycle: input.billing_cycle || 'monthly',
  priority: input.priority || 'medium',
  acquisition_source: input.acquisition_source || 'website',
  contract_duration: parseInt(input.contract_duration) || 6,
  payment_terms: {
    net_days: parseInt(input.payment_terms?.net_days) || 30,
    late_fee_percentage: parseFloat(input.payment_terms?.late_fee_percentage) || 1.5,
    auto_payment: Boolean(input.payment_terms?.auto_payment)
  },
  brand_colors: {
    primary: input.brand_colors?.primary || '#6366f1',
    secondary: input.brand_colors?.secondary || '#8b5cf6'
  },
  logo_url: input.logo_url || '',
  timezone: input.timezone || 'America/New_York',
  created_at: new Date().toISOString(),
  status: 'onboarding_initiated'
};

return [{ json: processedData }];

