// Combine all service results from both merge inputs
// Input 1: CRM & Welcome results
// Input 2: Portal & Billing results

const in1 = $items(0);
const in2 = $items(1);
const all = [...in1, ...in2];

return all.length ? all : [{ json: { warning: 'No service results received', timestamp: new Date().toISOString() } }];

// Process all service creation results and compile success/failure status
const items = $input.all();

let successCount = 0;
let failureCount = 0;
let services = [];
let errors = [];

items.forEach((item, index) => {
  if (item.json.error) {
    failureCount++;
    errors.push({
      service: item.json.service || `service_${index}`,
      error: item.json.error,
      timestamp: new Date().toISOString()
    });
  } else {
    successCount++;
    services.push({
      service: item.json.service || `service_${index}`,
      status: 'completed',
      result: item.json,
      timestamp: new Date().toISOString()
    });
  }
});

const onboardingStatus = {
  client_id: items[0]?.json?.client_id || items[0]?.json?.id,
  overall_status: failureCount === 0 ? 'success' : 'partial_success',
  completion_percentage: Math.round((successCount / (successCount + failureCount)) * 100),
  services_completed: successCount,
  services_failed: failureCount,
  total_services: successCount + failureCount,
  successful_services: services,
  failed_services: errors,
  next_steps: failureCount > 0 ? [
    'Review failed services and retry if needed',
    'Contact technical team for manual intervention',
    'Notify client of any service delays'
  ] : [
    'Send portal access credentials to client',
    'Schedule discovery call',
    'Begin project initiation phase'
  ],
  completion_timestamp: new Date().toISOString()
};

return [{ json: onboardingStatus }];

